public class Solution {
    public string LargestPalindrome(int n, int k) {
        if (k == 1) {
            return new string('9', n);
        }
        else if (k == 2) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            return "8" + new string('9', n - 2) + "8";
        }
        else if (k == 4) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";
            return "88" + new string('9', n - 4) + "88";
        }
        else if (k == 3 || k == 9) {
            return new string('9', n);
        }
        else if (k == 5) {
            if (n == 1) return "5";
            if (n == 2) return "55";
            return "5" + new string('9', n - 2) + "5";
        }
        else if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            if (n == 3) return "888";
            if (n == 4) return "8778";
            if (n == 5) return "89898";
            if (n == 6) return "897798";
            if (n == 7) return "8998998";
            if (n == 8) return "89977998";
            if (n % 2 == 0)
                return "8" + new string('9', (n - 4)/2) + "77" + new string('9', (n - 4)/2) + "8";
            else 
                return "8" + new string('9', (n - 3)/2) + "8" + new string('9', (n - 3)/2) + "8";
        }
        else if (k == 7) {
            if (n == 1) return "7";
            if (n == 2) return "77";
            if (n == 3) return "959";
            if (n == 4) return "9779";
            if (n == 5) return "99799";
            if (n == 6) return "999999";
            if (n == 7) return "9994999";
            for (char c = '9'; c >= '0'; c--) {
                if (n % 2 == 1) {
                    string s = new string('9', (n - 1)/2) + c + new string('9', (n - 1)/2); 
                    if (divisibleBy7(s)) return s;
                } else {
                    string s = new string('9', (n - 2)/2) + c + c + new string('9', (n - 2)/2); 
                    if (divisibleBy7(s)) return s;
                }
            }

        }
        else if (k == 8) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";
            if (n == 4) return "8888";
            if (n == 5) return "88888";
            if (n == 6) return "888888";
            return "888" + new string('9', n - 6) + "888";
        }

        return "";
    }

    private bool divisibleBy7(string s) {
        int num = 0;
        foreach (var c in s) {
            num = num * 10 + c - '0';
            num %= 7;
        }

        return num == 0;
    }


}
