class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1)
        {
            string res = "";
            for (int i = 0; i < n; ++i) res += "9";
            return res;
        }
        else if (k == 2)
        {            
            vector<int> vec = vector<int>(n, -1);
            vec[0] = 8;
            vec[n - 1] = 8;
            int lo = 1;
            int hi = n - 2;
            while (lo <= hi)
            {
                vec[lo] = 9;
                vec[hi] = 9;
                ++lo;
                --hi;
            }
            return ToStr(vec);
        }
        else if (k == 3 || k == 9)
        {
            vector<int> vec = vector<int>(n, -1);
            int sum = 0;          
            int lo = 0;
            int hi = n - 1;
            while (lo + 1 < hi)
            {
                vec[lo] = 9;
                vec[hi] = 9;
                sum += 9 + 9;
                sum %= k;
                ++lo;
                --hi;
            }
            if (n % 2 == 1)
            {
                for (int i = 9; i >= 0; --i)
                {
                    if ( (sum + i) % k == 0)
                    {
                        vec[n / 2] = i;
                        break;
                    }
                }
            }
            else
            {
                for (int i = 9; i >= 0; --i)
                {
                    if ( (sum + i + i) % k == 0)
                    {
                        vec[n / 2] = i;
                        vec[n / 2 - 1] = i;
                        break;
                    }
                }
            }

            return ToStr(vec);
        }
        else if (k == 4)
        {
            if (n == 1) return "8";
            else if (n <= 6)
            {
                int total = pow(10, n);                
                for (int i = total - 1; i >= 0; --i)
                {                    
                    vector<int> vec;
                    if (i % 4 == 0 && IsPalin(i, vec))
                    {
                        return ToStr(vec);
                    }
                }                
            }    
            else
            {
                vector<int> vec = vector<int>(n, -1);
                vec[0] = 8;
                vec[1] = 8;
                vec[n - 1] = 8;
                vec[n - 2] = 8;
                int lo = 2; 
                int hi = n - 3;
                while (lo <= hi)
                {
                    vec[lo] = 9;
                    vec[hi] = 9;
                    ++lo;
                    --hi;
                }
                return ToStr(vec);
            }        
        }
        else if (k == 5)
        {
            if (n == 1) return "5";
            else if (n == 2) return "55";
            else
            {
                vector<int> vec = vector<int>(n, -1);
                vec[0] = 5;
                vec[n - 1] = 5;
                int lo = 1; 
                int hi = n - 2;
                while (lo <= hi)
                {
                    vec[lo] = 9;
                    vec[hi] = 9;
                    ++lo;
                    --hi;
                }
                return ToStr(vec);
            }
        }
        else if (k == 6)
        {
            if (n == 1) return "6";
            else if (n <= 4)
            {
                int total = pow(10, n);                
                for (int i = total - 1; i >= 0; --i)
                {                    
                    vector<int> vec;
                    if (i % k == 0 && IsPalin(i, vec))
                    {
                        return ToStr(vec);
                    }
                }         
            }
            else
            {
                vector<int> vec = vector<int>(n, -1);            
                int64_t sum = 16;
                vec[0] = 8;
                vec[n - 1] = 8;
                int lo = 1;
                int hi = n - 2;
                while (lo + 1 < hi)
                {
                    vec[lo] = 9;
                    vec[hi] = 9;
                    sum += 9 + 9;
                    sum %= 3;
                    ++lo;
                    --hi;
                }
                if (n % 2 == 1)
                {
                    for (int i = 9; i >= 0; --i)
                    {
                        if ( (sum + i) % 3 == 0)
                        {
                            vec[n / 2] = i;
                            break;
                        }
                    }
                }
                else
                {
                    for (int i = 9; i >= 0; --i)
                    {
                        if ( (sum + i + i) % 3 == 0)
                        {
                            vec[n / 2] = i;
                            vec[n / 2 - 1] = i;
                            break;
