                    }
                }
                return ToStr(vec);
            }
        }
        else if (k == 7)
        {
            if (n == 1) return "7";
            else if (n == 2) return "77";
            else if (n == 3) return "959";
            else 
            {
                vector<int> vec = vector<int>(n, -1); 
                vec[0] = 9;
                vec[n - 1] = 9;
