                int hi = n - 2;
                while (lo + 1 < hi)
                {
                    vec[lo] = 9;
                    vec[hi] = 9;                    
                    ++lo;
                    --hi;
                }
                if (n % 2 == 1)
                {
                    for (int i = 9; i >= 0; --i)
                    {
                        vec[n / 2] = i;
                        int sum = 0;
                        for (int t = 0; t < vec.size(); ++t)
                        {
                            sum = sum * 10 + vec[t];
                            sum %= 7;
                        }
                        if (sum == 0)
                        {
                            return ToStr(vec);
                        }
                    }
                }
                else
                {
                    for (int i = 9; i >= 0; --i)
                    {
                        vec[n / 2] = i;
                        vec[n / 2 - 1] = i;
                        int sum = 0;
                        for (int t = 0; t < vec.size(); ++t)
                        {
