                for (int i = total - 1; i >= 0; --i)
                {                    
                    vector<int> vec;
                    if (i % k == 0 && IsPalin(i, vec))
                    {
                        return ToStr(vec);
                    }
                }         
            }          
            vector<int> vec = vector<int>(n, -1);
                vec[0] = 8;
                vec[1] = 8;
                vec[2] = 8;
                vec[n - 1] = 8;
                vec[n - 2] = 8;
                vec[n - 3] = 8;
                int lo = 3; 
                int hi = n - 4;
                while (lo <= hi)
                {
                    vec[lo] = 9;
                    vec[hi] = 9;
                    ++lo;
                    --hi;
                }
                return ToStr(vec);  
        }



        return "";
    }

    string ToStr(vector<int>& vec)
    {
        string temp = "";
        for (auto& d : vec) temp += '0' + d;
        return temp;
    }

    bool IsPalin(int val, vector<int>& vec)
    {
        int m = val;        
        while (m > 0)
        {
            vec.push_back(m % 10);
            m = m / 10;
        }
        int lo = 0;
        int hi = vec.size() - 1;
        while (lo < hi)
        {
            if (vec[lo] != vec[hi]) return false;
            ++lo;
            --hi;
        }
        return true;
    }
};
