class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<int> v(n, 9);

        if(k==1 || k==3 || k==9 || k==2 || k==4) {

            if(k==2) v[0] = 8, v[n-1] = 8;
            else if(k==4) {
                v[0] = 8, v[n-1] = 8; 
                if(n>=2) v[1] = 8, v[n-2] = 8;
            }

        } else if(k==5) {
            v[0] = 5, v[n-1] = 5;
        } else if(k==6) {
            if(n==1) v[0] = 6;
            else if(n==2) v[0] = v[n-1] = 6;
            else {
                v[0] = v[n-1] = 8;
                for(int i=1;i<n/2;i++) v[i] = v[n-i-1] = 9;

                if(n&1) v[n/2] = 8;
                else v[n/2-1] = v[n/2] = 7;
            }
        } else if(k==8) {
            if(n<=6) for(int i=0;i<n;i++) v[i] = 8;
            else {
                for(int i=0;i<3;i++) v[i] = v[n-i-1] = 8;
                for(int i=3;i<n-3;i++) v[i] = 9;
            }   
        } else if(k==7) {
            if(n<=2) for(int i=0;i<n;i++) v[i] = 7;
            else {
                if(n&1) {
                    map<int, int> mp;
                    mp[1] = 7, mp[5] = 7, mp[3] = 5, mp[7] = 4, mp[9] = 6, mp[11] = 4;
                    int t = n%12;
                    v[n/2] = mp[t];
                } else {
                    if(n%6 == 0) v[n/2-1] = v[n/2] = 9;
                    else if(n%12 == 2 || n%12 == 4) v[n/2-1] = v[n/2] = 7;
                    else v[n/2-1] = v[n/2] = 4;
                }
            }
        }

        string ans = "";
        for(int i=0;i<n;i++) ans += (v[i]+'0');
        return ans;
    }
};
