class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if (k == 1) or (k == 3) or (k == 9):
            return "9" * n
        if (k == 2):
            if n <= 2:
                return "8" * n
            else:
                return "8" + "9" * (n-2) + "8"
        if (k == 4):
            if n <= 4:
                return "8" * n
            else:
                return "8" * 2 + "9" * (n-4) + "8" * 2
        if (k == 8):
            if n <= 6:
                return "8" * n
            else:
                return "8" * 3 + "9" * (n-6) + "8" * 3
        if (k == 5):
            if n <= 2:
                return "5" * n
            else:
                return "5" + "9" * (n-2) + "5"
        if (k == 6):
            if n <= 2:
                return "6" * n
            elif n % 2 == 0:
                return "8" + "9" * (n//2-2) + "77"+ "9" * (n//2-2) + "8"
            else:
                return "8" + "9" * (n//2-1) + "8"+ "9" * (n//2-1) + "8"
        if (k == 7):
            so_du = [0,2,1,5,3,4][n%6]
            if n % 2 == 0:            
                do_dai_9 = (n//2-1)
                delta = (so_du * pow(5, do_dai_9%6) * 2) % 7
                return "9" * (n//2-1) + str(9 - delta) * 2 + "9" * (n//2-1)
            else:
                do_dai_9 = (n//2)
                delta = (so_du * pow(5, do_dai_9%6)) % 7
                return "9" * (n//2) + str(9 - delta) + "9" * (n//2)
