import sys

sys.set_int_max_str_digits(100000)

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        if k in [1, 3, 9]:
            return '9' * n
        if k == 2:
            return '8' + '9' * (n - 2)  + '8' if n >= 2 else '8'
        if k == 5:
            return '5' + '9' * (n - 2)  + '5' if n >= 2 else '5'
        if k == 4:
            return '88' + '9' * (n - 4)  + '88' if n >= 4 else '8' * n
        if k == 8:
            return '888' + '9' * (n - 6)  + '888' if n >= 6 else '8' * n
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n % 2 == 1:
                return '8' + '9' * ((n - 3) // 2) + '8' + '9' * ((n - 3) // 2) + '8'
            if n % 2 == 0:
                return '8' + '9' * ((n - 4) // 2) + '77' + '9' * ((n - 4) // 2) + '8'
        if k == 7:
            if n % 6 == 0:
                return '9' * n
            if n % 2 == 0:
                for i in range(9, -1, -1):
                    num = '9' * ((n - 2) // 2) + str(i) * 2 + '9' * ((n - 2) // 2)
                    if int(num) % 7 == 0:
                        return num
            else:
                for i in range(9, -1, -1):
                    num = '9' * ((n - 1) // 2) + str(i) + '9' * ((n - 1) // 2)
                    if int(num) % 7 == 0:
                        return num
