string calc(int n) {
    vector<int> rem(n, 1);
    for (int i = n-2; i >= 0; i--) {
        rem[i] = rem[i+1] * 10 % 7;
    }
    string ans(n, '9');
    int s = 0;
    for (int i = 0; i < (n-1)/2; i++) {
        s = (s + rem[i] * 9 + rem[n-1-i] * 9) % 7;
    }
    if (n & 1) {
        for (int i = 9; i >= 1; i--) {
            if ((s + rem[n/2] * i) % 7 == 0) {
                ans[n/2] = '0' + i;
                break;
            }
        }
    } else {
        for (int i = 9; i >= 1; i--) {
            if ((s + (rem[n/2] + rem[n/2-1]) * i) % 7 == 0) {
                ans[n/2] = ans[n/2-1] = '0' + i;
                break;
            }
        }
    }
    return ans;
}

class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) return string(n, '9');
        string ans(n, '9');
        if (k == 2) {
            ans[0] = ans[n-1] = '8';
            return ans;
        } else if (k == 4) {
            ans[0] = ans[n-1] = '8';
            if (n >= 2) ans[1] = ans[n-2] = '8';
            return ans;
        } else if (k == 8) {
            ans[0] = ans[n-1] = '8';
            if (n >= 2) ans[1] = ans[n-2] = '8';
            if (n >= 3) ans[2] = ans[n-3] = '8';
            return ans;
        } else if (k == 6) {
            if (n <= 2) return string(n, '6');
            ans[0] = ans[n-1] = '8';
            if (n & 1) ans[n/2] = '8';
            else ans[n/2-1] = ans[n/2] = '7';
            return ans;
        } else if (k == 5) {
            ans[0] = ans[n-1] = '5';
            return ans;
        } else {
            return calc(n);
        }
    }
};
