class Solution {
public:
    string largestPalindrome(int n, int k) {
        string result;
        if (k == 1) result = string(n, '9');
        if (k == 2) {
            result = '8';
            for (int i = 0; i < n - 2; ++i) result += '9';
            if (n >= 2) {
                result += '8';
            }
        }
        if (k == 3) {
            result = string(n, '9');
        }
        if (k == 4) {
            if (n <= 4) {
                result = string(n, '8');
            } else {
                result += "88";
                result += string(n - 4, '9');
                result += "88";
            }
        }
        if (k == 5) {
            result = '5';
            for (int i = 0; i < n - 2; ++i) result += '9';
            if (n >= 2) {
                result += '5';
            }
        }
        if (k == 6) {
            if (n <= 2) {
                result = string(n, '6');
            } else if (n == 3) {
                result = "888";
            } else {
                result += '8';
                if (n % 2 == 1) {
                    int n9 = (n - 3) / 2;
                    result += string(n9, '9');
                    result += '8';
                    result += string(n9, '9');
                } else {
                    int n9 = (n - 4) / 2;
                    result += string(n9, '9');
                    result += "77";
                    result += string(n9, '9');
                }
                result += '8';
            }
        }
        if (k == 7) {
            if (n <= 2) {
                return string(n, '7');
            } else {
                vector<int> res(n, 9);
                for (int digit = 9; digit >= 0; --digit) {
                    res[n / 2] = digit;
                    if (n % 2 == 0) {
                        res[n / 2 - 1] = digit;
                    }
                    int rem = 0;
                    for (int i = 0; i < n; ++i) {
                        rem = (10 * rem + res[i]) % 7;
                    }
                    if (rem == 0) {
                        for (int i = 0; i < n; ++i) {
                            result += '0' + res[i];
                        }
                        break;
                    }
                }
            }
        }
        if (k == 8) {
            if (n <= 6) {
                result = string(n, '8');
            } else {
                result += "888";
                result += string(n - 6, '9');
                result += "888";
            }
        }
        if (k == 9) {
            result = string(n, '9');
        }
        return result; 
    }
};
