class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def check7(s):
            rems = [1, 3, 2, 6, 4, 5]
            n = len(s)
            s = s[::-1]
            r = 0
            for i in range(n):
                r += int(s[i]) * rems[i%6]
                r %= 7
            return r == 0

        if n <= 4:
            for i in range(10**n-1, -1, -1):
                si = str(i)
                if i % k == 0 and si == si[::-1]:
                    return si

        if k in (1, 3, 9):
            return "9"*n

        if k == 2:
            return "8" + "9"*(n-2) + "8"

        if k == 5:
            return "5" + "9"*(n-2) + "5"

        if k == 4:
            return "88" + "9"*(n-4) + "88"

        if k == 8:
            if n == 5:
                return "88888"
            else:
                return "888" + "9"*(n-6) + "888"

        if k == 6:
            if n % 2 == 0:
                return "8" + "9"*(n//2 - 2) + "77" + "9"*(n//2 - 2) + "8"
            else:
                return "8" + "9"*(n//2 - 1) + "8" + "9"*(n//2 - 1) + "8"

        if k == 7:
            s = ["9"]*n
            for d in range(9, -1, -1):
                s[n//2] = str(d)
                s[(n-1)//2] = str(d)
                if check7(s):
                    return "".join(s)
