class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s="";
        for(int i=0;i<n;i++) s+="9";
        if(k==1) ;
        else if(k==2){
            s[0]=s[n-1]='8';
        }
        else if(k==3) ;
        else if(k==4){
            for(int i=0;i<min(2,n);i++) s[i]='8';
            for(int i=n-1;i>=max(0,n-2);i--) s[i]='8';
        }
        else if(k==5) s[0]=s[n-1]='5';
        else if(k==6) {
            if(n==1) s[0]='6';
            else if(n==2) s[0]=s[1]='6';
            else{
                s[0]=s[n-1]='8';
                if(n&1) s[n/2]='8';
                else {
                    s[n/2]=s[n/2-1]='7';
                }
            }
        }
        else if(k==7){
            for(int i=0;i<n;i++) s[i]='9';
            int a[]={1,3,2,6,4,5};
            int sum=0;
            for(int i=0;i<n;i++) {
                sum+=a[i%6];
            }
            int x=a[n/2%6];
            if(n&1){
                sum-=a[n/2%6];
            }
            else {
                sum-=a[n/2%6]+a[(n/2-1)%6];
                x+=a[(n/2-1)%6];
            }
            sum=sum%7*9%7;
            for(int i=9;i>=0;i--){
                if((sum+x*i)%7==0){
                    if(n&1) s[n/2]=i+'0';
                    else s[n/2]=s[n/2-1]=i+'0';
                    break;
                }
            }

        }
        else if(k==8){
            for(int i=0;i<min(n,3);i++) s[i]='8';
            for(int i=n-1;i>=max(n-3,0);i--) s[i]='8';
        }
        else if(k==9);
        return s;
    }
};
