class Solution {
public:
    bool dv(const string &num) {
        long long remainder = 0;
        for (char digit : num) {
            remainder = (remainder * 10 + (digit - '0')) % 7;
        }
        return remainder == 0;
    }
    string largestPalindrome(int n, int k) {
        string s;

        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) s += '9';
            return s;
        }
        else if (k == 2) {
            if (n == 1) return "8";
            s += '8';
            for (int i = 1; i < n - 1; i++) s += '9';
            s += '8';
            return s;
        }
        else if (k == 4) {
            if (n < 4) {
                for (int i = 0; i < n; i++) s += '8';
                return s;
            }
            s += "88";
            for (int i = 2; i < n - 2; i++) s += '9';
            s += "88";
            return s;
        }
        else if (k == 5) {
            if (n == 1) return "5";
            s += '5';
            for (int i = 1; i < n - 1; i++) s += '9';
            s += '5';
            return s;
        }
        else if (k == 6) {
            if (n < 3) return string(n,'6');
            if(n&1){
                s += '8';
                for (int i = 1; i < n - 1; i++) s += '9';
                s += '8';
                s[n/2]='8';
                return s;
            }
            else{
                int sum=-2;
                s += '8';
                for (int i = 1; i < n - 1; i++) s += '9', sum+=9;
                s += '8';
                for(char i='9';i>='0';i--){
                    s[n/2]=i; s[n/2-1]=i;
                    if((sum+2*(i-'0'))%3==0) return s;
                }
            }
        }
        else if (k == 7) {
            if (n<3) return string(n,'7');
            s += '9';
            for (int i = 1; i < n - 1; i++) s += '9';
            s += '9'; 
            if(n&1){
                for(char i='9';i>='0';i--){
                    s[n/2]=i;
                    if(dv(s)) return s;
                }
            }
            else{
                for(char i='9';i>='0';i--){
                    s[n/2]=i; s[n/2-1]=i;
                    if(dv(s)) return s;
                }
            }
        }
        else if (k == 8) {
            if(n < 6){
                return string(n,'8');
            }
            s += "888";
            for(int i=0;i<n-6;i++) s+='9';
            s+="888";
            return s;
        }
        return s;
    }
};
