class Solution {
    public static boolean isDivisibleBy7(String numberStr) {
        int n = 0;
        for (int i = 0; i < numberStr.length(); i++) {
            char digitChar = numberStr.charAt(i);
            int digit = Character.getNumericValue(digitChar);
            n = (n * 10 + digit) % 7;
        }
        return n == 0;
    }

    public static boolean isDivisibleBy3(String numberStr) {
        int n = 0;
        for (int i = 0; i < numberStr.length(); i++) {
            n += numberStr.charAt(i) - '0';
        }
        return n % 3 == 0;
    }

    public String largestPalindrome(int n, int k) {
        StringBuilder stringBuilder = new StringBuilder();
        switch (k) {
            case 1, 3, 9 -> {
                stringBuilder.append("9".repeat(Math.max(0, n)));
                break;
            }
            case 2 -> {
                stringBuilder.append("8");
                if (n > 1) {
                    stringBuilder.append("9".repeat(Math.max(0, n - 2)));
                    stringBuilder.append("8");
                }
                break;
            }
            case 4 -> {
                if (n <= 4) {
                    stringBuilder.append("8".repeat(Math.max(0, n)));
                } else {
                    int mid9 = n - 4;
                    stringBuilder.append("88");
                    stringBuilder.append("9".repeat(Math.max(0, mid9)));
                    stringBuilder.append("88");
                }
                break;
            }
            case 5 -> {
                stringBuilder.append("5");
                if (n > 1) {
                    stringBuilder.append("9".repeat(Math.max(0, n - 2)));
                    stringBuilder.append("5");
                }
                break;
            }
            case 6 -> {
                if (n == 1) {
                    stringBuilder.append("6");
                } else if (n == 2) {
                    stringBuilder.append("66");
                } else if (n == 4) {
                    stringBuilder.append("8778");
                } else if ((n & 1) == 1) {
                    int left9 = (n - 3) / 2;
                    stringBuilder.append("8");
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    stringBuilder.append("9"); // 不明确
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    stringBuilder.append("8");
                    for (int i = 9; i >= 0; i--) {
                        stringBuilder.setCharAt(left9 + 1, (char) ('0' + i));
                        if (isDivisibleBy3(stringBuilder.toString())) {
                            break;
                        }
                    }
                } else {
                    int left9 = (n - 4) / 2;
                    stringBuilder.append("8");
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    stringBuilder.append("99"); // 不明确
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    stringBuilder.append("8");
                    for (int i = 9; i >= 0; i--) {
                        stringBuilder.setCharAt(left9 + 1, (char) ('0' + i));
                        stringBuilder.setCharAt(left9 + 2, (char) ('0' + i));
                        if (isDivisibleBy3(stringBuilder.toString())) {
                            break;
                        }
                    }
                }
                break;
            }
            case 7 -> {
                if (n == 1 || n == 2) {
                    stringBuilder.append("7".repeat(Math.max(0, n)));
                } else if ((n & 1) == 1) {
                    int left9 = (n - 1) / 2;
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    stringBuilder.append("0");
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    for (int i = 9; i >= 0; i--) {
                        stringBuilder.setCharAt(left9, (char) ('0' + i));
                        if (isDivisibleBy7(stringBuilder.toString())) {
                            break;
                        }
                    }
                } else {
                    int left9 = (n - 2) / 2;
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    stringBuilder.append("00");
                    stringBuilder.append("9".repeat(Math.max(0, left9)));
                    for (int i = 9; i >= 0; i--) {
                        stringBuilder.setCharAt(left9, (char) ('0' + i));
                        stringBuilder.setCharAt(left9 + 1, (char) ('0' + i));
                        if (isDivisibleBy7(stringBuilder.toString())) {
                            break;
                        }
                    }
                }
                break;
            }
            case 8 -> {
                if (n <= 6) {
                    stringBuilder.append("8".repeat(Math.max(0, n)));
                } else {
                    int mid9 = n - 6;
                    stringBuilder.append("888");
                    stringBuilder.append("9".repeat(Math.max(0, mid9)));
                    stringBuilder.append("888");
                }
                break;
            }
        }
        return stringBuilder.toString();
    }
}
