import sys
sys.set_int_max_str_digits(100000)

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1 or k==3 or k==9:
            return "9"*n
        elif k==2:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            else:
                return "8"+"9"*(n-2)+"8"
        elif k==4:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            elif n==3:
                return "888"
            else:
                return "88"+"9"*(n-4)+"88"
        elif k==5:
            if n==1:
                return "5"
            elif n==2:
                return "55"
            else:
                return "5"+"9"*(n-2)+"5"
        elif k==6:
            if n==1:
                return "6"
            elif n==2:
                return "66"
            else:
                if n%2:
                    curr = (8+9*(n//2-1))*2
                    if curr%3==0:
                        return "8"+"9"*(n-2)+"8"
                    elif curr%3==1:
                        return "8"+"9"*(n//2-1)+"8"+"9"*(n//2-1)+"8"
                    else:
                        return "8"+"9"*(n//2-1)+"7"+"9"*(n//2-1)+"8"
                else:
                    curr = 16 + 18*(n//2-2)
                    if curr%3==0:
                        return "8"+"9"*(n-2)+"8"
                    elif curr%3==1:
                        return "8"+"9"*(n//2-2)+"77"+"9"*(n//2-2)+"8"
                    else:
                        return "8"+"9"*(n//2-2)+"88"+"9"*(n//2-2)+"8"
        elif k==7:
            if n==1:
                return "7"
            elif n==2:
                return "77"
            else:
                if n%2:
                    for i in range(9,-1,-1):
                        a = "9"*(n//2)+str(i)+"9"*(n//2)
                        if int(a)%7==0:
                            return str(a)
                else:
                    for i in range(9,-1,-1):
                        a = "9"*(n//2-1)+str(i)+str(i)+"9"*(n//2-1)
                        if int(a)%7==0:
                            return str(a)
                    return "9"*n
        elif k==8:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            elif n==3:
                return "888"
            elif n==4:
                return "8888"
            elif n==5:
                return "88888"
            elif n==6:
                return "888888"
            else:
                return "888"+"9"*(n-6)+"888"
