class Solution {
public:
    int sol(int i,int rem,int &n,int &k,
            vector<vector<int>>&mem,
            vector<vector<int>>&pow){
        int &ret=mem[i][rem];
        if(i>n-i-1)
            return ret=rem>0?1e7:0;

        if(~ret)
            return ret;
        ret=1e7;
        for (int j = 9; j; --j) {
            if(i==n-i-1)
                ret=min(ret,sol(i+1,(rem+pow[i+1][j])%k,n,k,
                                mem,pow));
            else   ret=min(ret,sol(i+1,(rem+pow[i+1][j]+pow[n-i][j])%k,n,k,
                                   mem,pow));

        }
        return ret;
    }
    void bld(int i,int rem,int &n,int &k,string &pref,string &suf,
                     vector<vector<int>>&mem,
                     vector<vector<int>>&pow){
        if(i>n-i-1)
            return ;
        int &ret=mem[i][rem];
        for (int j = 9; j; --j) {
            if(i==n-i-1){
                if(ret==sol(i+1,(rem+pow[i+1][j])%k,n,k,mem,pow)){
                pref+=char(j+'0');
                bld(i+1,(rem+pow[i+1][j])%k,n,k,pref,suf,
                    mem,pow);
                break;
                }
            }

            else if(ret==sol(i + 1, (rem + pow[i + 1][j] + pow[n -i][j]) % k, n, k,mem, pow)){
                pref+=char(j+'0');
                suf+=char(j+'0');
                bld(i+1,(rem+pow[i + 1][j] + pow[n -i][j])%k,n,k,pref,suf,mem,pow);
                break;
            }

        }
    }
    string largestPalindrome(int n, int k) {
        vector<vector<int>>pow(n+1,vector<int>(10,1));
        int ten=1;
        for (int i = 1; i <=n; ++i) {
            for (int j = 1; j <10 ; ++j) {
                pow[i][j]=ten*j%k;
            }
            ten=ten*10%k;
        }
        vector<vector<int>>mem(n+2,vector<int>(11,-1));
        string pref,suf;
        sol(0,0,n,k,mem,pow);
        bld(0,0,n,k,pref,suf,mem,pow);
        std::reverse(suf.begin(), suf.end());
        pref+=suf;
        return pref;
    }
};
