class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1 || k==9 || k==3){
            string ans="";
            for(int i=0;i<n;i++) ans+='9';
            return ans;
        }
        else if(k==5){
            string ans="";
            for(int i=0;i<n;i++) ans+='9';
            ans[0]='5';
            ans[n-1]='5';
            return ans;
        }
        else if(k==2){
            string ans="";
            for(int i=0;i<n;i++) ans+='9';
            ans[0]='8';
            ans[n-1]='8';
            return ans;
        }
        // 8
        else if(k==4){
            string ans="";
            for(int i=0;i<n;i++) ans+='9';
            ans[0]='8';
            ans[n-1]='8';
            if(n>2){
                ans[1]='8';
                ans[n-2]='8';
            }
            return ans;
        }
        else if(k==6){
            string ans="";
            if(n==1) return "6";
            if(n==2) return "66";
            for(int i=0;i<n;i++) ans+='9';
            ans[0]='8';
            ans[n-1]='8';
            if(n%2){
                ans[n/2]='8';
            }
            else{
                ans[n/2-1]='7';
                ans[n/2]='7';
            }
            return ans;
        }
        else if(k==8){
            string ans="";
            if(n==1) return "8";
            if(n==2) return "88";
            if(n==3) return "888";
            if(n==4) return "8888";
            if(n==5) return "88888";
            if(n==6) return "888888";
            for(int i=0;i<n;i++) ans+='9';
            ans[0]=ans[1]=ans[2]=ans[n-1]=ans[n-2]=ans[n-3]='8';
            return ans;
        }
        else{
            if(n%2){
                int x=n/2;
                int an=0;
                int poten=1;
                int save=-1;
                for(int i=0;i<n;i++){
                    if(i==n/2){
                        save=poten;
                        poten=poten*10;
                        poten%=7;
                        continue;
                    }
                    int cur=9*poten;
                    cur=cur%7;
                    an=(an+cur)%7;
                    poten=(poten*10)%7;
                }
                int zz=0;
                for(int i=0;i<=9;i++){
                    if((an+i*save)%7==0){
                        zz=i;
                    }
                }
                string ans="";
                for(int i=0;i<n;i++) ans+='9';
                ans[n/2]=(char)('0'+zz);
                return ans;
            }
            else{
                int x1=n/2, x2=n/2-1;
                int an=0;
                int poten=1;
                int save=-1;
                for(int i=0;i<n;i++){
                    if(i==n/2-1){
                        save=poten;
                        poten=poten*100;
                        poten%=7;
                        i++;
                        continue;
                    }
                    int cur=9*poten;
                    cur=cur%7;
                    an=(an+cur)%7;
                    poten=(poten*10)%7;
                }
                int zz=0;
                for(int i=0;i<=99;i+=11){
                    if((an+i*save)%7==0){
                        zz=i;
                    }
                }
                string req=to_string(zz);
                if(req.size()<2) req='0'+req;
                string ans="";
                for(int i=0;i<n/2-1;i++) ans+='9';
                return ans+req+ans;
            }
        }
        return "00";
    }
};
