class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<int> po10(n, 1);
        for (int i = 1; i < n; i++) {
            po10[i] = (10 * po10[i-1]) % k;
        }

        vector<vector<bool>> can(n, vector<bool>(k));
        for (int l = (n - 1) / 2, r = n / 2; l >= 0; l--, r++) {
            if (l == r) {
                for (int x = 0; x <= 9; x++) {
                    can[l][(po10[l] * x) % k] = true;
                }
            } else if (l == r-1) {
                for (int x = 0; x <= 9; x++) {
                    can[l][((po10[l] + po10[r]) * x) % k] = true;
                }
            } else {
                for (int x = 0; x <= 9; x++) {
                    for (int he = 0; he < k; he++) if (can[l+1][he]) {
                        // if (l == 1) cout << po10[l] << ' ' << ' ' << po10[r] << ' ' << he << endl;
                        can[l][((po10[l] + po10[r]) * x + he) % k] = true;
                        // cout << "> " << l << ' ' << ((po10[l] + po10[r]) + he) % k << endl;
                    }
                }
            }
        }

        // if (n == 5) cout << can[2][0] << ' ' << can[1][0] << endl;
        int prv_sum = 0;
        string ans(n, '?');
        for (int l = 0, r = n-1; l <= r; l++, r--) {
            for (int x = 9; x >= 0; x--) {
                int new_sum = ((po10[l] + po10[r]) * x) % k;
                if (l == r) new_sum = po10[l] * x % k;
                if (l == r || l == r-1) {
                    if ((prv_sum + new_sum) % k == 0) {
                        ans[l] = ans[r] = char('0' + x);
                        break;
                    }
                } else {
                    if (can[l+1][(2 * k - prv_sum - new_sum) % k]) {
                        ans[l] = ans[r] = char('0' + x);
                        prv_sum = (prv_sum + new_sum) % k;
                        break;
                    }
                }
            }
        }
        return ans;
    }
};
