typedef long long ll;

class Solution
{
public:
    string largestPalindrome(int n, int k)
    {
        vector<int> pw(n + 1);
        pw[0] = 1;
        for (int i = 1; i <= n; ++i)
            pw[i] = pw[i - 1] * 10 % k;

        int m = (n + 1) / 2;

        vector f(m + 1, vector<int>(10, 0));
        vector pre(m + 1, vector<pair<int, int>>(10, {0, 0}));
        f[0][0] = 1;

        for (int i = 0; i < m; ++i) {
            for (int j = 0; j <= 9; ++j) {
                int to;

                if (n % 2 == 1 && i == 0)
                    to = j * pw[m - 1] % k;
                else
                    to = j * (pw[(m - 1) - i] + pw[n - 1 - ((m - 1) - i)]) % k;

                for (int t = 0; t <= 9; ++t)
                    if (f[i][t]) {
                        f[i + 1][(t + to) % k] = 1;
                        if (pre[i + 1][(t + to) % k].first < j)
                            pre[i + 1][(t + to) % k] = {j, t};
                    }
            }
        }

        string ans;
        int A = m, B = 0;
        while (A > 0) {
            ans += pre[A][B].first + '0';
            B = pre[A][B].second;
            A--;
        }
        for (int i = (int)ans.size() - 1 - (n % 2 == 1); i >= 0; --i)
            ans += ans[i];
        return ans;
    }
};
