class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if (k == 1 or k == 3 or k == 9):
            return '9' * n
        elif (k == 2):
            if (n == 1):
                return '8'
            elif (n == 2):
                return '88'
            else:
                return '8' + '9' * (n - 2) + '8'
        elif (k == 4):
            if (n == 1):
                return '8'
            elif (n == 2):
                return '88'
            elif (n == 3):
                return '888'
            else:
                return '88' + '9' * (n - 4) + '88'
        elif (k == 5):
            if (n == 1):
                return '5'
            elif (n == 2):
                return '55'
            else:
                return '5' + '9' * (n - 2) + '5'
        elif (k == 6):
            if (n == 1):
                return '6'
            elif (n == 2):
                return '66'
            elif (n == 3):
                return '888'
            elif (n == 4):
                return '8778'
            else:
                if (n % 2 == 1):
                    return '8' + (n-3)//2 * '9' + '8' + (n-3)//2 * '9' + '8'
                else:
                    return '8' + (n-4)//2 * '9' + '77' + (n-4)//2 * '9' + '8'
        elif (k == 8):
            if (n <= 6):
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'
        else:
            if (k == 1):
                return '7'
            elif (k == 2):
                return '77'
            else:
                if (n % 2 == 1):
                    for x in range(9, -1, -1):
                        residue = 0
                        power = 1
                        for y in range(n):
                            if (y != n //2):
                                residue = (9 * power + residue) % 7
                            else:
                                residue = (x * power + residue) % 7
                            power *= 10
                            power %= 7

                        if (residue == 0):
                            return (n) //2 * '9' + str(x) + (n) //2 * '9'
                else:

                    for x in range(9, -1, -1):
                        residue = 0
                        power = 1
                        for y in range(n):
                            if (y != n //2 and y != n//2 - 1):
                                residue = (9 * power + residue) % 7
                            else:
                                residue = (x * power + residue) % 7
                            power *= 10
                            power %= 7
                        if (residue == 0):
                            #print(x)
                            return (n - 2) //2 * '9' + str(x) * 2 + (n - 2) //2 * '9'
                    return None
