class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return "9" * n
        elif k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"
        elif k == 3:
            return "9" * n
        elif k == 4:
            if n < 5:
                return "8" * n
            return "88" + "9" * (n - 4) + "88"
        elif k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n - 2) + "5"
        elif k == 6:
            if n < 3:
                return "6" * n
            if n % 2 == 1:
                return "8" + "9" * ((n - 3) // 2) + "8" + "9" * ((n - 3) // 2) + "8"
            else:
                return "8" + "9" * (n // 2 - 2) + "77" + "9" * (n // 2 - 2) + "8"
        elif k == 7:
            if n < 3:
                return "7" * n
            if n % 2 == 1:
                for x in range(9, -1, -1):
                    v = int("9" * (n // 2) + str(x) + "9" * (n // 2))
                    if v % 7 == 0:
                        return str(v)
            else:
                for x in range(9, -1, -1):
                    v = int("9" * (n // 2 - 1) + str(x) + str(x) + "9" * (n // 2 - 1))
                    if v % 7 == 0:
                        return str(v)
        elif k == 8:
            if n < 7:
                return "8" * n
            return "888" + "9" * (n - 6) + "888"
        elif k == 9:
            return "9" * n
