class Solution {
public:
    bool check(string s)
    {
        int len=s.size(),num=0;
        for (int i = 0; i < len; i++) num = (num * 10 + s[i] - '0') % 7;
        cout<<s<<" "<<num<<endl;
        return !num;
    }
    string largestPalindrome(int n, int k) {
        string s;
        if(k==1||k==3||k==9)
        {
            string s1;
            for(int i=0;i<n;++i)s1.push_back('9');
            return s1;
        }
        else if(k==5)
        {
            string s5="5";
            if(n==1)return s5;
            for(int i=1;i<n-1;++i)s5.push_back('9');
            s5.push_back('5');
            return s5;
        }
        else if(k==2)
        {
            string s2="8";
            if(n==1)return s2;
            for(int i=1;i<n-1;++i)s2.push_back('9');
            s2.push_back('8');
            return s2;
        }
        else if(k==4)
        {
            if(n==1)return "8";
            else if(n==2)return "88";
            else if(n==3)return "888";
            string s4="88";
            for(int i=3;i<=n-2;++i)
            {
                s4.push_back('9');
            }
            s4+="88";
            return s4;
        }
        else if(k==8)
        {
            if(n==1)return "8";
            else if(n==2)return "88";
            else if(n==3)return "888";
            else if(n==4)return "8888";
            else if(n==5)return "88888";
            string s4="888";
            for(int i=4;i<=n-3;++i)
            {
                s4.push_back('9');
            }
            s4+="888";
            return s4;
        }
        else if(k==6)
        {
            if(n==1)return "6";
            else if(n==2)return "66";
            string s6="8";
            if(n==1)return s6;
            for(int i=1;i<n-1;++i)s6.push_back('9');
            s6.push_back('8');
            int sum=8*2+9*(n-2);
            if(n%2)
            {
                int pos=n/2;
                s6[pos]-=sum%3;
            }
            else{
                int pos2=n/2;
                int pos1=(n-1)/2;
                if(sum%3==2)s6[pos1]--,s6[pos2]--;
                else if(sum%3==1)s6[pos1]-=2,s6[pos2]-=2;
            }return s6;
        }
        else{
            if(n==1)return "7";
            else if(n==2)return "77";
            string s7;
            for(int i=1;i<=n;++i)s7.push_back('9');
            if(n%2){
                int pos=n/2;
                for(int i=0;i<=9;++i)
                {
                    s7[pos]='9'-i;
                    if(check(s7))return s7;
                }
            }
            else{
                int pos1=(n-1)/2,pos2=n/2;
                for(int i=0;i<=9;++i)
                {
                    s7[pos1]='9'-i,s7[pos2]='9'-i;
                    if(check(s7))return s7;
                }
            }
        }
        return "";
    }
};
