class Solution {
public:
    int sevenRem(int p) {
        if (p % 6 == 0) {
            return 0;
        }
        if (p % 6 == 1) {
            return 2;
        }
        if (p % 6 == 2) {
            return 1;
        }
        if (p % 6 == 3) {
            return 5;
        }
        if (p % 6 == 4) {
            return 3;
        }
        if (p % 6 == 5) {
            return 4;
        }
        return -1;
    }
    string largestPalindrome(int n, int k) {
        char c[n + 1];
        c[n] = '\0';
        if (k == 1 || k == 3 || k == 9) {
            for(int i = 0; i < n; ++i) {
                c[i] = '9';
            }
            return c;
        }
        if (k == 2) {
            if (n == 1) {
                return "8";
            }
            c[0] = '8';
            for(int i = 1; i < n - 1; ++i) {
                c[i] = '9';
            }
            c[n - 1] = '8';
            return c;
        }
        if (k == 4) {
            if (n == 1) {
                return "8";
            }
            if (n == 2) {
                return "88";
            }
            if (n == 3) {
                return "888";
            }
            c[0] = '8';
            c[1] = '8';
            for(int i = 2; i < n - 2; ++i) {
                c[i] = '9';
            }
            c[n - 2] = '8';
            c[n - 1] = '8';
            return c;
        }
        if (k == 5) {
            if (n == 1) {
                return "5";
            }
            c[0] = '5';
            for(int i = 1; i < n - 1; ++i) {
                c[i] = '9';
            }
            c[n - 1] = '5';
            return c;
        }
        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n == 3) {
                return "888";
            }
            if (n == 4) {
                return "8778";
            }
            if (n == 5) {
                return "89898";
            }
            if (n % 2 == 1) {
                c[0] = '8';
                for(int i = 0; i < n / 2 - 1; ++i) {
                    c[i + 1] = '9';
                }
                c[n / 2] = '8';
                for(int i = 0; i < n / 2 - 1; ++i) {
                    c[n / 2 + i + 1] = '9';
                }
                c[n - 1] = '8';
                return c;
            }
            c[0] = '8';
            for(int i = 0; i < n / 2 - 2; ++i) {
                c[i + 1] = '9';
            }
            c[n / 2 - 1] = '7';
            c[n / 2] = '7';
            for(int i = 0; i < n / 2 - 2; ++i) {
                c[i + n / 2 + 1] = '9';
            }
            c[n - 1] = '8';
            return c;
        }
        if (k == 7) {
            if (n == 1) {
                return "7";
            }
            if (n == 2) {
                return "77";
            }
            if (n == 3) {
                return "959";
            }
            if (n == 4) {
                return "9779";
            }
            if (n == 5) {
                return "99799";
            }
            if (n == 6) {
                return "999999";
            }
            int rem = sevenRem(n);
            if (n % 2 == 1) {
                for(int i = 0; i < n / 2; ++i) {
                    c[i] = '9';
                }
                c[n / 2] = '9';
                while (rem % 7 > 0) {
                    --c[n / 2];
                    rem += 7;
                    rem -= sevenRem(n / 2) + 1;
                }
                for(int i = n / 2 + 1; i < n; ++i) {
                    c[i] = '9';
                }
                return c;
            }
            for(int i = 0; i < n / 2 - 1; ++i) {
                c[i] = '9';
            }
            c[n / 2 - 1] = '9';
            c[n / 2] = '9';
            while (rem % 7 > 0) {
                --c[n / 2 - 1];
                --c[n / 2];
                rem += 14;
                rem -= sevenRem(n / 2 - 1) + 1;
                rem -= sevenRem(n / 2) + 1;
            }
            for(int i = n / 2 + 1; i < n; ++i) {
                c[i] = '9';
            }
            return c;
        }
        if (k == 8) {
            if (n == 1) {
                return "8";
            }
            if (n == 2) {
                return "88";
            }
            if (n == 3) {
                return "888";
            }
            if (n == 4) {
                return "8888";
            }
            if (n == 5) {
                return "88888";
            }
            c[0] = '8';
            c[1] = '8';
            c[2] = '8';
            for(int i = 3; i < n - 3; ++i) {
                c[i] = '9';
            }
            c[n - 3] = '8';
            c[n - 2] = '8';
            c[n - 1] = '8';
            return c;
        }
        return "";
    }
};
