class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n <= 4:
            for i in range(pow(10, n) - 1, pow(10, n - 1), -1):
                if i % k == 0:
                    temp = str(i)
                    temp1 = temp[::-1]
                    if temp == temp1:
                        return str(i)
        else:
            if k == 1:
                return "9" * n
            elif k == 2:
                return "8" + "9" * (n - 2) + "8"
            elif k == 3:
                return "9" * n
            elif k == 4:
                return "88" + "9" * (n - 4) + "88"
            elif k == 5:
                return "5" + "9" * (n - 2) + "5"
            elif k == 6:
                if n & 1:
                    return "8" + "9" * (n // 2 - 1) + "8" + "9" * (n // 2 - 1) + "8"
                else:
                    return "8" + "9" * (n // 2 - 2) + "77" + "9" * (n // 2 - 2) + "8"
            elif k == 7:
                if n & 1:
                    temp = 0
                    tt = n // 2
                    tu = 1
                    tuo = 0
                    for i in range(n):
                        if i != tt:
                            temp = (temp + tu * 9) % k
                            tu = (tu * 10) % k
                        else:
                            tuo = tu
                            tu = (tu * 10) % k
                    for p in range(9, -1, -1):
                        if (temp + p * tuo) % k == 0:
                            return "9" * tt + str(p) + "9" * tt

                else:
                    temp = 0
                    tt = n // 2
                    tu = 1
                    tuo = 0
                    for i in range(n):
                        if i != tt - 1 and i != tt:
                            temp = (temp + tu * 9) % k
                            tu = (tu * 10) % k
                        elif i == tt - 1:
                            tuo = tu
                            tu = (tu * 10) % k
                        else:
                            tu = (tu * 10) % k
                    for p in range(9, -1, -1):
                        if (temp + p * tuo + p * tuo * 10) % k == 0:
                            return "9" * (tt - 1) + str(p) + str(p) + "9" * (tt - 1)
            elif k == 8:
                if n <= 6:
                    return "8" * n
                else:
                    return "888" + "9" * (n - 6) + "888"
            elif k == 9:
                return "9" * n



