class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        base = "9"*n
        if k == 1 or k == 3 or k == 9:
            return base
        if k == 2:
            if n <= 2:
                return "8"*n
            else:
                return "8" + base[1:n-1]+"8"
        if k == 4:
            if n <= 4:
                return "8"*n
            else:
                return "88" + base[2:n-2]+"88"
        if k == 8:
            if n <= 6:
                return "8"*n
            else:
                return "888"+base[3:n-3]+"888"
        if k == 5:
            if n <= 2:
                return "5"*n
            else:
                return "5" + base[1:n-1]+"5"
        if k == 6:
            if n <= 2:
                return "6"*n
            elif n == 3:
                return "888"
            else:
                if n % 2 == 1:
                    return "8" + "9"*((n-3)//2) + "8" + "9"*((n-3)//2) + "8"
                else:
                    return "8" + "9"*((n-4)//2) + "77" + "9"*((n-4)//2) + "8"
        if k == 7:
            if n <= 2:
                return "7"*n
            mods = [1]*n
            for i in range(1, n):
                mods[i] = (mods[i-1]*10)%7
            nines = (n-1)//2
            if n % 2 == 0:
                modseven = 0
                for i in range(nines):
                    modseven += 9*mods[i] + 9*mods[n-i-1]
                modseven %= 7
                for i in range(9, 0, -1,):
                    if (mods[nines]*i+mods[nines+1]*i+modseven) % 7 == 0:
                        return "9"*nines+ str(i)*2+"9"*nines
            else:
                modseven = 0
                for i in range(nines):
                    modseven += 9*mods[i] + 9*mods[n-i-1]
                modseven %= 7
                for i in range(9, 0, -1,):
                    if (mods[nines]*i+modseven) % 7 == 0:
                        return "9"*nines+ str(i)+"9"*nines
        return ""
