class Solution {
public:
using L = long long;

    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9){
            return string(n, '9');
        }else if(k == 2){
            if(n == 1) return "8";
            else return "8" + string(n-2, '9') + "8";
        }else if(k == 4){
            if(n == 1)return "8";
            else if(n == 2) return "88";
            else if(n == 3) return "888";
            else if(n == 4) return "8888";
            else return "88" + string(n-4, '9') + "88";
        }else if(k == 5){
            if(n == 1) return "5";
            else return "5" + string(n-2, '9') + "5";
        }else if(k == 6){
            if(n == 1){
                return "6";
            }else if(n == 2) return "66";
            else if(n == 3) return "888";
            else if(n == 4) return "8778";
            else if(n == 5) return "89898";
            else if(n == 6) return "897798";
            else if(n == 7) return "8998998";
            else{
                if(n % 2 == 0) return "8" + string((n-4)/2, '9') + "77" + string((n-4)/2, '9') + "8";
                else return "8" + string((n-3)/2, '9') + "8" + string((n-3)/2, '9') + "8";
            }
        }else if(k == 7){
            vector<string> s = {"5", "77", "7", "99", "4", "44", "6", "44", "4", "99", "7", "77"};
            if(n == 1)return "7";
            else if(n==2) return "77";
            else{
                string str = s[(n-3) % s.size()];
                return string((n-str.size())/2, '9') + str + string((n-str.size())/2, '9');
            }

        }else if(k == 8){
            if(n <= 6) return string(n, '8');
            else return "888" + string(n-6, '9') + "888";
        }
        return "";
    }
};
