    typedef long long ll;
    ll Remainder(string str, int R)
    {
        // len is variable to store the
        // length of Number string.
        ll len = str.size();

        ll Num, Rem = 0;

        // loop that find Remainder
        for (int i = 0; i < len; i++) {

            Num = Rem * 10 + (str[i] - '0');
            Rem = Num % R;
        }

        // Return the remainder
        return Rem;
    }
