class Solution {
public:
    bool isdiv(string s){
        int now=0;
        for(int i=0;i<s.size();i++){
            now*=10;
            now+=(s[i]-'0');
            now%=7;
        }
        return (now==0);
    }
    string largestPalindrome(int n, int k) {
        string ans="";
        if(n==1){
            if(k>=5){
                return to_string(k);
            }
            else {
                if(k==1)return "9";
                if(k==2)return "8";
                if(k==3)return "9";
                if(k==4)return "8";
            }
        }
        if(k==1||k==3||k==9){
            for(int i=0;i<n;i++)ans+='9';
        }
        else if(k==2){
            ans+='8';
            for(int i=1;i<n-1;i++)ans+='9';
            if(n>=2)ans+='8';
        }
        else if(k==4){
            ans+='8';
            if(n>=2)ans+='8';
            for(int i=2;i<n-2;i++)ans+='9';
            if(n>=3)ans+='8';
            if(n>=4)ans+='8';
        }
        else if(k==5){
            ans+='5';
            for(int i=1;i<n-1;i++)ans+='9';
            if(n>=2)ans+='5';
        }
        else if(k==6){
            if(n==2){
                return "66";
            }
            if(n%2){
                for(int i=0;i<n;i++){
                    if(i==0||i==n-1||i==n/2){
                        ans+='8';
                    }
                    else ans+='9';
                }
            }
            else{
                for(int i=0;i<n;i++){
                    if(i==0||i==n-1){
                        ans+='8';
                    }
                    else if(i==(n-1)/2||i==(n+1)/2){
                        ans+='7';
                    }
                    else ans+='9';
                }
            }
        }
        else if(k==7){
            for(int i=0;i<n;i++){
                ans+='9';
            }
            for(char z='9';z>=0;z--){
                ans[n/2]=z;ans[(n-1)/2]=z;
                if(isdiv(ans)){
                    return ans;
                }
            }
        }
        else if(k==8){
            for(int i=0;i<n;i++){
                if(i<=2||i>=n-3){
                    ans+='8';
                }
                else ans+='9';
            }
        }
        return ans;
    }
};
