class Solution {
public:

    bool check(string &str, int end) {
        if(end==1)
            return ((str[0]-'0')*10+str[1]-'0')%7==0;

        int sub=(str[end]-'0')*2;
        if((str[end-1]-'0')>=sub)
            str[end-1]-=sub;
        else
        {
            if(end-2>=0)
            {
                int lastNum=(str[end-2]-'0')*10+str[end-1]-'0';
                int diff=lastNum-sub;
                str[end-2]=diff/10+'0';
                str[end-1]=diff%10+'0';
            }
            else
                return false;
        }

        return check(str, end-1);
    }
    string largestPalindrome(int n, int k) {
        string ans;
        if(k==1 || k==3 || k==9)
        {
            for(int i=0;i<n;i++)
                ans+='9';
        }
        else if(k==2)
        {
            if(n==1)
                ans="8";
            else if(n==2)
                ans="88";
            else
            {
                ans+='8';
                for(int i=0;i<n-2;i++)
                    ans+='9';
                ans+='8';
            }
        }
        else if(k==4)
        {
            if(n==1)
                ans="8";
            else if(n==2)
                ans="88";
            else if(n==3)
                ans="888";
            else
            {
                ans+="88";
                for(int i=0;i<n-4;i++)
                    ans+='9';
                ans+="88";
            }
        }
        else if(k==5)
        {
            if(n==1)
                ans="5";
            else if(n==2)
                ans="55";
            else
            {
                ans+='5';
                for(int i=0;i<n-2;i++)
                    ans+='9';
                ans+='5';
            }
        }
        else if(k==8)
        {
            if(n==1)
                ans="8";
            else if(n==2)
                ans="88";
            else if(n==3)
                ans="888";
            else if(n==4)
                ans="8888";
            else if(n==5)
                ans="88888";
            else if(n==6)
                ans="888888";
            else
            {
                ans+="888";
                for(int i=0;i<n-6;i++)
                    ans+='9';
                ans+="888";
            }
        }
        else if(k==6)
        {
            if(n==1)
                ans="6";
            else if(n==2)
                ans="66";
            else
            {
                if(n%2==1)
                {
                    int sum=8;
                    ans+='8';
                    for(int i=0;i<n/2-1;i++)
                    {
                        ans+='9';
                        sum+=9;
                    }
                    sum*=2;
                    if(sum%3==0)
                        ans+='9';
                    else if(sum%3==1)
                        ans+='8';
                    else
                        ans+='7';
                    for(int i=0;i<n/2-1;i++)
                        ans+='9';
                    ans+='8';
                }
                else
                {
                    int sum=8;
                    ans+='8';
                    for(int i=0;i<n/2-2;i++)
                    {
                        ans+='9';
                        sum+=9;
                    }
                    sum*=2;
                    if(sum%3==0)
                        ans+="99";
                    else if(sum%3==1)
                        ans+="77";
                    else
                        ans+="88";
                    for(int i=0;i<n/2-2;i++)
                        ans+='9';
                    ans+='8';
                }
            }
        }
        else if(k==7)
        {
            if(n==1)
                ans="7";
            else if(n==2)
                ans="77";
            else
            {
                if(n%2==0)
                {
                    for(int j='9';j>='0';j--)
                    {
                        string temp1, temp2;
                        for(int i=0;i<n/2-1;i++)
                        {
                            temp1+='9';
                            temp2+='9';
                        }

                        temp1+=j;
                        temp2+=j;
                        temp1+=j;
                        temp2+=j;
                        for(int i=0;i<n/2-1;i++)
                        {
                            temp1+='9';
                            temp2+='9';
                        }
                        if(check(temp1, temp1.size()-1))
                        {
                            ans=temp2;
                            break;
                        }
                    }
                }
                else
                {
                    for(int j='9';j>='0';j--)
                    {
                        string temp1, temp2;
                        for(int i=0;i<n/2;i++)
                        {
                            temp1+='9';
                            temp2+='9';
                        }

                        temp1+=j;
                        temp2+=j;
                        for(int i=0;i<n/2;i++)
                        {
                            temp1+='9';
                            temp2+='9';
                        }
                        if(check(temp1, temp1.size()-1))
                        {
                            ans=temp2;
                            break;
                        }
                    }
                }
            }
        }
        return ans;
    }
};
