import sys
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # return largest integer w/ n digits that is 
        # palindrome & divisible by k
        sys.set_int_max_str_digits(10**6)
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            if n <= 2:
                return '8'*n
            else:
                return '8' + '9'*(n - 2) + '8'
        if k == 4:
            if n <= 4:
                return '8'*n
            else:
                nines = n - 4
                return '88' + '9'*nines + '88'
        if k == 8:
            if n <= 6:
                return '8'*n
            else:
                nines = n - 6
                return '888' + '9'*nines + '888'
        if k == 5:
            if n <= 2:
                return '5'*n
            else:
                return '5' + '9'*(n - 2) + '5'
        if k == 7:
            if n <= 2:
                return '7'*n
            curr = int('9'*n)
            for i in range(11):
                if curr % 7 == 0:
                    return str(curr)
                if n % 2 == 0:  # replace middle two
                    curr -= 10**(n // 2)
                    curr -= 10**(n // 2 - 1)
                else:  # just replace middle
                    curr -= 10**(n // 2)
        if k == 6:
            if n <= 2:
                return '6'*n
            elif n == 3:
                return '888'
            elif n % 2 == 0:
                nines = n // 2 - 2
                return '8' + '9'*nines + '77' + '9'*nines + '8'
            else:
                nines = n // 2 - 1
                return '8' + '9'*nines + '8' + '9'*nines + '8'
