class Solution {
public:

    string max139(int n)
    {
        string ans = "";
        for (int i=0;i<n;i++)
        {
            ans += '9';
        }
        return ans;
    }

    string max2(int n)
    {
        if (n == 1)
        {
            return "8";
        }
        string ans = "8";
        n -= 2;
        for (int i=0;i<n;i++)
        {
            ans += '9';
        }
        ans += "8";
        return ans;
    }

    string max4(int n)
    {
        if (n < 4)
        {
            string ans = "";
            for (int i=0;i<n;i++)
            {
                ans += '8';
            }
            return ans;
        }
        string ans = "88";
        n -= 4;
        for (int i=0;i<n;i++)
        {
            ans += '9';
        }
        ans += "88";
        return ans;
    }

    string max5(int n)
    {
        if (n == 1)
        {
            return "5";
        }
        string ans = "5";
        n -= 2;
        for (int i=0;i<n;i++)
        {
            ans += '9';
        }
        ans += "5";
        return ans;
    }

    string max8(int n)
    {
        if (n < 6)
        {
            string ans = "";
            for (int i=0;i<n;i++)
            {
                ans += '8';
            }
            return ans;
        }
        string ans = "888";
        n -= 6;
        for (int i=0;i<n;i++)
        {
            ans += '9';
        }
        ans += "888";
        return ans;
    }

    string max6(int n)
    {
        if (n == 1)
        {
            return "6";
        }
        if (n == 2)
        {
            return "66";
        }
        if (n == 3)
        {
            return "888";
        }
        if (n == 4)
        {
            return "8778";
        }
        if (n == 5)
        {
            return "89898";
        }
        if (n == 6)
        {
            return "897798";
        }
        if (n % 2 == 1)
        {
            int half = n/2;
            string ans = "8";
            for (int i=1;i<half;i++)
            {
                ans += "9";
            }
            ans += ans;
            ans += "8";
            return ans;
        }
        int half = n/2;
        string ans = "8";
        for (int i=2;i<half;i++)
        {
            ans += "9";
        }
        ans += "77";
        for (int i=2;i<half;i++)
        {
            ans += "9";
        }
        ans += "8";
        return ans;
    }

    string max7(int n)
    {
        if (n == 1) {
            return "7";
        } 
        if (n == 2)
        {
            return "77";
        } 

        vector<int> div(n);
        div[0] = 1;
        for (int i = 1; i < n; i++)
        {
            div[i] = (div[i-1] * 10) % 7;
        }

        string ans(n, '9');
        int cur = 0;
        for (int i = 0; i < n; i++)
        {
            cur = (cur + 9 * div[i]) % 7;
        }

        if (cur == 0) return ans;

        if (n % 2 == 1)
        {
            int mid = n / 2;
            cur = (cur - (div[mid] * 9) % 7 + 7) % 7;
            int need = (7 - cur) % 7;
            for (int i = 8; i >= 0; i--)
            {
                if ((div[mid] * i) % 7 == need)
                {
                    ans[mid] = i + '0';
                    return ans;
                }
            }
            return "-1";
        }
        else
        {
            int mid = n / 2 - 1;
            int cf = (div[mid] + div[mid+1]) % 7;
            cur = (cur - (cf * 9) % 7 + 7) % 7;
            int need = (7 - cur) % 7;
            for (int i = 8; i >= 0; i--)
            {
                if ((cf * i) % 7 == need)
                {
                    ans[mid] = ans[mid+1] = i + '0';
                    return ans;
                }
            }
            return "-2";
        }
        return "-2";
    }

    string largestPalindrome(int n, int k) {
        if (k==1 || k == 3 || k == 9)
        {
            return max139(n);
        }
        if (k == 2)
        {
            return max2(n);
        }
        if (k==4)
        {
            return max4(n);
        }
        if (k==5)
        {
            return max5(n);
        }
        if (k==8)
        {
            return max8(n);
        }
        if (k == 6)
        {
            return max6(n);
        }
        if (k == 7)
        {
            return max7(n);
        }
        return "-3";
    }
};
