#define ll long long
class Solution {
    bool check(string s){
        int i = 0,j = s.size()-1;
        while(i<j){
            if(s[i]!=s[j])return false;
            ++i,--j;
        }
        return true;
    }
public:
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9){
            return string(n,'9');
        }
        if(k == 2){
            if(n < 2)return to_string(8);
            string ans = "8";
            ans += string(max(0,n-2),'9');
            ans += '8';
            return ans;
        }
        if(k == 5){
            if(n < 2)return to_string(5);
            string a = "";
            a += '5';
            a += string(max(0,n-2),'9');
            a += '5';
            return a;
        }
        if(n <= 6){
            int j = pow(10,n)-1;
            while(j%k || !check(to_string(j))){
                --j;
            }
            return to_string(j);
        }
        if(k == 4){
            string st = "";
            st += '8';
            st += '8';
            st += string(max(0,n-4),'9');
            st += '8';
            st += '8';
            return st;
        }
        if(k == 6){
            string res = "8";
            ll sum = 8;
            for(int i=1;i<n-1;++i){
                sum  = (sum + 9);
                res += '9';
            }
            if(n > 1){
                res += '8';
                sum += 8;
            }
            while(sum %3 ){
                if(n%2){
                    res[n/2]--;
                    sum--;
                }
                else if(n%2 == 0){
                    int hlf = n/2;
                    int hlf2 = hlf-1;
                    sum -= 2;
                    res[hlf]--;
                    res[hlf2]--;
                }
            }
            return res;
        }
        string res = string(n,'9');
        if(k == 8){
            string ans = "888";
            string rev = ans;
            reverse(begin(rev),end(rev));
            for(int i=6;i<n;++i){
                ans += '9';
            }
            ans += rev;
            return ans;
        }
        while(1){
                ll sum = 0;
                for(int i=0;i<n;++i){
                    sum = (sum*10 + (res[i]-'0'))%k;
                }
                if(sum == 0)return res;
                if(n%2){
                    res[n/2]--;
                    sum--;
                }
                else if(n%2 == 0){
                    int hlf = n/2;
                    int hlf2 = hlf-1;
                    sum -= 2;
                    res[hlf]--;
                    res[hlf2]--;
                }
            }
        return "";
    }
};
