class Solution {
public:
    bool Check(const string& curr, int k) {
        int rem=0;
        for(char c : curr) {
            rem = (rem*10+c-'0')%k;
        }
        return rem==0;
    }

    bool ispalin(const string& S) {
        int n=S.size();
        for(int i=0;i<n;i++) {
            if (S[i]!=S[n-1-i]) return false;
        }
        return true;
    }


    string largestPalindrome(int n, int k) {

        if (n <= 5) {
            string ans;
            for(int x=1;;x++) {
                string S = to_string(x);
                if (S.size()>n) break;
                if (S.size()==n && ispalin(S) && Check(S,k)) ans=S;
            }
            return ans;
        }

        if (k==9) {
            return string(n,'9');
        }

        if (k==8) {
            if (n<=6) {
                return string(n,'8');
            } else {
                return string(3, '8')+string(n-6,'9')+string(3,'8');
            }
        }

        if (k==7) {
            for(int d=9;d>=0;d--) {
                string curr = string((n+1)/2-1,'9')+string(1+(n%2==0),d+'0')+string((n+1)/2-1,'9');
                if (Check(curr, k)) return curr;
            }
        }

        if (k==6) {
            for(int d=9;d>=0;d--) {
                string curr = string(1,'8')+string((n-3)/2,'9')+string(1+(n%2==0),d+'0')+string((n-3)/2,'9')+string(1,'8');
                if (Check(curr, k)) return curr;
            }
        }

        if (k==5) {
            return string(1,'5')+string(n-2,'9')+string(1,'5');
        }

        if (k==4) {
            return string(2,'8')+string(n-4,'9')+string(2,'8');
        }

        if (k==3) {
            return string(n,'9');
        }

        if (k==2) {
            return string(1,'8')+string(n-2,'9')+string(1,'8');
        }
        if (k==1) {
            return string(n,'9');
        }


        return "";


    }
};
