sys.set_int_max_str_digits(2 * 10 ** 5 + 1)

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        m = n // 2
        if n == 1:
            for ii in range(9, -1, -1):
                if ii % k == 0:
                    return str(ii)
        # print(m)
        if n % 2 == 0:
            for ii in range(10 ** m - 1, 10**(m-1) - 1, -1):
                num = str(ii) + str(ii)[::-1]
                if k == 5:
                    num = "5" + num[1:]
                    num = num[:-1] + '5'
                elif k == 8 and n >= 3:
                    num = "888" + num[3:]
                    num = num[:-3] + "888"

                elif k == 4 and n > 2:
                    num = "88" + num[2:]
                    num = num[:-2] + "88"
                elif k in [2, 6] and n > 2:
                    num = "8" + num[1:]
                    num = num[:-1] + "8"
                if int(num) % k == 0:
                    return num
        else:
            for ii in range(10 ** m - 1, 10**(m-1) - 1, -1):
                for jj in range(9, -1, -1):
                    num = str(ii) + str(jj) + str(ii)[::-1]
                    if k == 5:
                        num = "5" + num[1:]
                        num = num[:-1] + '5'
                    elif k == 8 and n >= 3:
                        num = "888" + num[3:]
                        num = num[:-3] + "888"
                    elif k == 4 and n > 2:
                        num = "88" + num[2:]
                        num = num[:-2] + "88"
                    elif k in [2, 6] and n > 2:
                        num = "8" + num[1:]
                        num = num[:-1] + "8"
                    if int(num) % k == 0:
                        return num
