class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        int n2 = n/2;
        int odd = n%2;
        for(int i = 0; i < n; i++) ans.push_back('9');
        if(k == 2){
            ans[0] = '8';
            ans[n - 1] = '8';
        }
        else if(k == 4){
            ans[0] = '8';
            ans[n - 1] = '8';
            if(n > 2){
               ans[1] = '8';
               ans[n - 2] = '8';    
            }
        }
        else if(k == 5){
            ans[0] = '5';
            ans[n - 1] = '5';
        }
        else if(k == 6){
            if(n < 3){
                ans[0] = '6';
                ans[n - 1] = '6';
            }
                else{
                ans[0] = '8';
                ans[n - 1] = '8';
                if(odd) ans[n/2] = '8';
                else{
                    ans[n/2 - 1] = '7';
                    ans[n/2]  = '7';
                }
            }
        }
        else if(k == 7){
            if(n%12 == 1 || n%12 == 5) {
                ans[n/2] = '7';
            }
            else if(n%12 == 2 || n%12 == 4){
                ans[n/2 - 1] = '7';
                ans[n/2] = '7';
            }
            else if(n%12 == 3){
                ans[n/2] = '5';
            }
            else if(n%12 == 7 || n%12 == 11){
                ans[n/2] = '4';
            }
            else if(n%12 == 8 || n%12 == 10){
                ans[n/2] = '4';
                ans[n/2 - 1] = '4';
            }
            else if(n%12 == 9){
                ans[n/2] = '6';
            }

        }
        else if(k == 8){
            ans[0] = '8';
            ans[n - 1] = '8';
            if(n > 2){
                ans[1] = '8';
                ans[n - 2] = '8';  
                if(n > 4){
                    ans[2] = '8';
                    ans[n - 3] = '8';
                }
            }
        }
        return ans;
    }
};
