import sys
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(200000)
        if k == 1 or k == 9 or k == 3:  # 1, 9, 3
            return '9' * n

        if k == 2:  # 2
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            return '8' + '9' * (n - 2) + '8'

        if k == 5:  # 5
            if n == 1:
                return "5"
            if n == 2:
                return "55"
            return '5' + '9' * (n - 2) + '5'

        if k == 4:  # 4
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            if n == 3:
                return "888"
            return "88" + '9' * (n - 4) + "88"

        if k == 8:  # 8
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            if n == 3:
                return "888"
            if n == 4:
                return "8888"
            if n == 5:
                return "88888"
            return "888" + '9' * (n - 6) + "888"

        if k == 6:  # 6
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            if n % 2 == 0:
                return "8" + '9' * ((n - 4) // 2) + "77" + '9' * ((n - 4) // 2) + "8"
            else:
                return "8" + '9' * ((n - 3) // 2) + "8" + '9' * ((n - 3) // 2) + "8"
        if k == 7: # 7
            if n == 1:
                return "7"
            if n == 2:
                return "77"
            if n % 2 == 0:
                for i in range(9, 0, -1):
                    ss = '9' * ((n - 2) // 2) + str(i) + str(i) + '9' * ((n - 2) // 2)
                    if int(ss) % 7 == 0:
                        return ss
            else:
                for i in range(9, 0, -1):
                    ss = '9' * ((n - 1) // 2) + str(i) + '9' * ((n - 1) // 2)
                    if int(ss) % 7 == 0:
                        return ss
        return ""
