class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in (1, 3, 9):
            return "9" * n
        if k == 2:
            if n < 2: return "8"
            return "8" + '9' * (n - 2) + "8"
        if k == 4:
            if n <= 4:
                return "8" * n
            return "88" + '9' * (n - 4) + "88"
        if k == 8:
            if n <= 6:
                return "8" * n
            return "888" + '9' * (n - 6) + "888"
        if k == 5:
            if n < 2: return "5"
            return "5" + '9' * (n - 2) + "5"
        if k == 6:
            if n <= 2: return "6" * n
            s = list("8" + '9' * (n - 2) + "8")
            if n % 2 == 0:
                s[n // 2] = '7'
                s[n // 2 - 1] = '7'
            else:
                s[n // 2] = '8'
            return "".join(s)
        if k == 7:
            if n <= 2: return "7" * n
            s = list("9" * n)
            ss = 0
            if n % 2 == 0:
                for i in range(n):
                    if i not in (n // 2, n // 2 - 1):
                        ss = (ss * 10 + 9) % 7
                    else:
                        ss = (ss * 10) % 7
            else:
                for i in range(n):
                    if i != (n // 2):
                        ss = (ss * 10 + 9) % 7
                    else:
                        ss = (ss * 10) % 7
            for i in range(9, -1, -1):
                if n % 2 == 0:
                    s[n // 2] = str(i)
                    s[n // 2 - 1] = str(i)
                    if (ss + (i * 10 ** (n // 2)) + (i * 10 ** (n // 2 - 1))) % 7 == 0:
                        return "".join(s)
                else:
                    s[n // 2] = str(i)
                    if (ss + i * 10 ** (n // 2)) % 7 == 0:
                        return "".join(s)
