#define ll long long
class Solution {
public:

const static int N=1e5+5;
int dp[N][10][10];
ll po[N];

bool func(int ind,int first,int last,int n,int k){
    if(dp[ind][first][last]!=-1) return dp[ind][first][last];
    if(ind==(n-1)/2+1){
        return dp[ind][first][last]=((first+last)%k==0);
    }
    bool ans=0;
    for(int i=9;i>=0;i--){
        int newf=first,newl=last;
        newf=(first+po[n-ind-1]*i)%k;
        if(ind!=n/2){
            newl=(last+po[ind]*i)%k;
        }
        ans|=func(ind+1,newf,newl,n,k);
        if(ans) break;
    }
    return dp[ind][first][last]=ans;
}

