class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        if ((k==1)||(k==3)||(k==9)){
            ans = string(n,'9');
        }
        if (k==2){
            if(n==1){
                ans = "8";
            }
            else{
                ans = "8" + string(n-2,'9') + "8";
            }
        }
        if (k==4){
            if(n<4){
                ans = string(n,'8');
            }
            else{
                ans = "88" + string(n-4,'9') + "88";
            }
        }
        if (k==5){
            if(n==1){
                ans = "5";
            }
            else{
                ans = "5" + string(n-2,'9') + "5";
            }
        }
        if (k==6){
            if(n<3){
                ans = string(n,'6');
            }
            else{
                if(n%2==1){
                    ans = "8" + string((n-3)/2, '9') + "8" + string((n-3)/2, '9') + "8";
                }
                else{
                    ans = "8" + string((n-4)/2, '9') + "77" + string((n-4)/2, '9') + "8";
                }
            }
        }
        if (k==8){
            if(n<6){
                ans = string(n,'8');
            }
            else{
                ans = "888" + string(n-6,'9') + "888";
            }
        }
        if (k==7){
            if (((n%12)==1)||((n%12)==2)){
                ans = string((n-1)/2,'9') + string(n%12,'7') + string((n-1)/2,'9');
            }
            else if ((n%12)==3){
                ans = string((n-1)/2,'9') + "5" + string((n-1)/2,'9');
            }
            else if((n%12)==4){
                ans = string((n-1)/2,'9') + "77" + string((n-1)/2,'9');
            }
            else if((n%12)==5){
                ans = string((n-1)/2,'9') + "7" + string((n-1)/2,'9');
            }
            else if((n%6)==0){
                ans = string(n,'9');
            }
            else if((n%12)<9){
                ans = string((n-1)/2,'9') + string(n%6,'4') + string((n-1)/2,'9');
            }
            else if((n%12)==9){
                ans = string(n/2,'9') + "6" + string(n/2,'9');
            }
            else{
                ans = string((n-1)/2,'9') + string(6-n%6,'4') + string((n-1)/2,'9');
            }
        }
        return ans;
    }
};
