int isPal(int x)
{
    string s=to_string(x);
    ll n=s.length();
    for(int i=0;i<n;i++)
    {
        if(s[i]!=s[n-1-i]) return 0;

    }
        return 1;
}
int isDivisible(string s)
{
    int idx=0;
    ll rem=0;
    while(idx<s.length())
    {
        rem*=10;
        rem+=s[idx]-'0';
        idx++;
        rem%=7;
    }
    if(rem==0) return 1;
    return 0;

}
