class Solution {
    bool test7(string& s) {
        int rem = 0;
        for (char& ch : s) {
            rem *= 10;
            rem += ch - '0';
            rem %= 7;
        }
        return (rem == 0);
    }
public:
    string largestPalindrome(int n, int k) {
        string res;
        if (k == 1 || k == 3 || k == 9) {
            res = string(n, '9');
        } else if (k == 2) {
            res = string(n, '9');
            res[0] = res[n - 1] = '8';
        } else if (k == 4) {
            if (n == 1) {
                res = "8";
            } else {
                res = string(n, '9');
                res[0] = res[1] = res[n - 2] = res[n - 1] = '8';
            }
        } else if (k == 5) {
            res = string(n, '9');
            res[0] = res[n - 1] = '5';
        } else if (k == 6) {
            if (n < 3) {
                res = string(n, '6');
            } else if (n & 1) {
                res = string(n, '9');
                res[0] = res[n - 1] = res[n / 2] = '8';
            } else {
                res = string(n, '9');
                res[0] = res[n - 1] = '8';
                int p = n / 2;
                res[p - 1] = res[p] = '7';
            }
        } else if (k == 8) {
            if (n < 3) {
                res = string(n, '8');
            } else {
                res = string(n, '9');
                res[0] = res[1] = res[2] = res[n - 3] = res[n - 2] = res[n - 1] = '8';
            }
        } else {
            res = string(n, '9');
            for (char i = '9'; i >= '0'; i--) {
                if (n & 1) {
                    res[n / 2] = i;
                } else {
                    res[(n / 2) - 1] = res[n / 2] = i;
                }
                if (test7(res)) {
                    break;
                }
            }
        }
        return res;
    }
};
