class Solution {
public:
    int val[100005];
    char ans[100005];
    bool dfs(int l,int r,int sum,int mod){
      //  printf("%d %d %d\n",sum,l,r);
        if(l==r){
            for(int i = 9;i>=0;i--){
                if((sum+val[l]*i)%mod==0){
                    ans[l]=i+'0';
                    return true;
                }
            }
            return false;
        }


        else if(l+1==r){
            for(int i = 9;i>=0;i--){
                if((sum+(val[l]+val[r])*i)%mod==0){
                    ans[l]=ans[r]=i+'0';
                    return true;
                }
            }
            return false;
        }
        else{
            for(int i = 9;i>=0;i--){
                ans[l]=ans[r]=i+'0';
                if(dfs(l+1,r-1,(sum+(val[l]+val[r])*i)%mod,mod))return true;
            }
            return false;
        }
    }
    string largestPalindrome(int n, int k) {

        val[0]=1;
        for(int i=1;i<n;i++){
            val[i]=val[i-1]*10%k;
        }
        if(k==2){
            string str;
            str+="8";
            if(n==1)return str;
            for(int i = 1;i<n-1;i++)str+="9";
            str+="8";
            return str;
        }
        if(k==4){
            string str;
            str+="8";
            if(n==1)return str;
            if(n==2)return "88";
            if(n==3)return "888";
            str+="8";
            for(int i = 2;i<n-2;i++)str+="9";
            str+="88";
            return str;
        }
        if(k==8){
            string str;
            if(n<=6){
                string str;
                for(int i = 0;i<n;i++)str+="8";
                return str;
            }
            str+="888";
            for(int i=3;i<n-3;i++)str+="9";
            str+="888";
            return str;
        }
        if(k==5){
            string str;
            str+="5";
            if(n==1)return str;
            for(int i = 1;i<n-1;i++)str+="9";
            str+="5";
            return str;
        }
        if(n==1){
            if(k==1)return "9";
            if(k==3)return "9";
            else{
                string str;
                str+=(char)k+'0';
                return str;
            }
        }
        if(n==2){
            if(k==1)return "99";
            if(k==3)return "99";
            else{
         string str;
                str+=(char)k+'0';
                    str+=(char)k+'0';
                return str;
            }
        }
        int l=0,r=n-1;
        int sum=0;
        if(k==6&&n==2)return "66";
        if(k==6){
            ans[l]=ans[r]='8';
            sum+=(val[l]+val[r])*8;
        }
        else{
            ans[l]=ans[r]='9';
            sum+=(val[l]+val[r])*9;
        }
        sum%=k;
        dfs(l+1,r-1,sum,k);
        string str;
        for(int i = 0;i<n;i++)str+=ans[i];
        return str;
    }
};
