#define fo(i, n) for (int i = 0; i < n; ++i)
#define foback(i, n) for (int i = n - 1; i >= 0; --i)
#define fo1(i, n) for (ll i = 1; i <= n; ++i)

#define all(x) x.begin(), x.end()
#define rall(x) x.rbegin(), x.rend()
#define make_unique(x) sort(all(x)), x.resize(unique(all(x)) - x.begin())

typedef long long ll;

bool ispali(string& s) {
    // auto s = to_string(x);
    string ss(s);
    reverse(all(ss));
    return s == ss;
}

class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s;

        if (n <= 6) {
            s = "0";
            ll ans=0;
            for(ll i=1;; i++) {
                if (i%k > 0) continue;

                auto x = to_string(i);
                if (x.size() > n) break;
                if (ispali(x)) {
                    s = x;
                }
            }

            return s;
        }

        ll r=0;
        vector<ll> d(n);
        ll p = 1;
        fo(i,n) {
            s += '9';
            r += p*9 % k;
            r %= k;

            d[i] = p;

            p *= 10;
            p %= k;
        }

        // for(char z='9'; )

        if (k==1) {
            return s;
        } else if (k==2) {
            s[0] = s[n-1] = '8';
            return s;
        } else if (k==4) {
            s[0] = s[1] = s[n-2] = s[n-1] = '8';
            return s;
        } else if (k==5) {
            s[0] = s[n-1] = '5';
            return s;
        } else if (k==6) {
            s[0] = s[n-1] = '8';


            // r -= d[0];
            // r = (r+k)%k;
            // r -= d.back();
            // r = (r+k)%k;

            if (n&1) {
                s[n/2] = '8';
            } else {
                s[n/2] = s[n/2-1] = '7';
            }
            return s;    
        } else if (k==3) {  
            return s;

        } else if (k==9) {
            return s;
        } else if (k==7) {

            while(r > 0) {
                if (n&1) {
                    s[n/2]--;
                    r -= d[n/2];
                    r = (r+k)%k;
                } else {
                    s[n/2]--;
                    s[n/2-1]--;
                    r -= d[n/2];
                    r = (r+k)%k;
                    r -= d[n/2-1];
                    r = (r+k)%k;
                }
            }
            return s;  
        } else if (k==8) {
            fo(i,3) s[i] = s[n-1-i] = '8';
        }   

        return s;
    }
};
