# define ll long long
class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(k == 1){
            for(int i = 0; i < n; i++) ans += '9';
            return ans;
        }
        if(k == 2){
            if(n == 1) return "8";
            for(int i = 0; i < n; i++) ans += '9';
            ans[0] = '8';
            ans[n-1] = '8';
            return ans;
        }
        if(k == 3){
            for(int i = 0; i < n; i++) ans += '9';
            return ans;
        }
        if(k == 4){
            if(n == 1) return "8";
            if(n == 2) return "88";
            if(n == 3) return "888";
            for(int i = 0; i < n; i++) ans += '9';
            ans[0] = '8';
            ans[1] = '8';
            ans[n-1] = '8';
            ans[n-2] = '8';
            return ans;
        }
        if(k == 5){
            for(int i = 0; i < n; i++) ans += '9';
            ans[0] = '5';
            ans[n-1] = '5';
            return ans;
        }
        if(k == 6){
            if(n == 1) return "6";
            if(n == 2) return "66";
            for(int i = 0; i < n; i++) ans += '9';
            ans[n-1] = '8';
            ans[0] = '8';
            if(n % 2 == 1){
                for(char ch = '9'; ch >= '0'; ch--){
                    ans[n/2] = ch;
                    ll sum = 0;
                    for(int j = 0;j<n; ++j){
                        sum += ans[j] - '0';
                    }
                    if(sum % 3 == 0) {
                        return ans;
                    }
                }
            }
            if(n % 2 == 0){
                for(char ch = '9'; ch >= '0'; ch--){
                    ans[n/2] = ch;
                    ans[n/2-1] = ch;
                    ll sum = 0;
                    for(int j = 0;j<n; ++j){
                        sum += ans[j] - '0';
                    }
                    if(sum % 3 == 0) {
                        return ans;
                    }
                }
            }
        }
        if(k == 7){
            if(n == 1) return "7";
            if(n == 2) return "77";
            for(int i = 0; i < n; i++) ans += '9';
            if(n % 2 == 1){
                string maxRes = "";
                for(char ch = '9'; ch >= '0'; ch--){
                    ans[n/2] = ch;
                    for(char ch1 = '9'; ch1 >= '0'; ch1--){
                        ans[n/2-1] = ch1;
                        ans[n/2+1] = ch1;
                        string res = "";
                        res += ch1; res += ch; res += ch1;
                        // check if res is divisible by 7
                        ll sum = ans[0] - '0';
                        for(int j = 0;j<n-1; ++j){
                            sum *= 3;
                            sum += ans[j+1] - '0';
                            sum %= 7;
                        }
                        if(sum % 7 == 0) {
                            if(maxRes == "" || stoll(res) > stoll(maxRes)){
                                maxRes = res;
                            }
                        }
                    }
                }
                ans[n/2-1] = maxRes[0];
                ans[n/2] = maxRes[1];
                ans[n/2+1] = maxRes[2];
                return ans;
            } else {
                string maxRes = "";
                for(char ch = '9'; ch >= '0'; ch--){
                    ans[n/2] = ch;
                    ans[n/2-1] = ch;
                    for(char ch1 = '9'; ch1 >= '0'; ch1--){
                        ans[n/2+1] = ch1;
                        ans[n/2-2] = ch1;
                        string res = "";
                        res += ch1; res += ch; res += ch; res += ch1;
                        ll sum = ans[0] - '0';
                        for(int j = 0;j<n-1; ++j){
                            sum *= 3;
                            sum += ans[j+1] - '0';
                            sum %= 7;
                        }
                        if(sum % 7 == 0) {
                            if(maxRes == "" || stoll(res) > stoll(maxRes)){
                                maxRes = res;
                            }
                        }
                    }
                }
                ans[n/2-2] = maxRes[0];
                ans[n/2-1] = maxRes[1];
                ans[n/2] = maxRes[2];
                ans[n/2+1] = maxRes[3];
                return ans;
            }
        }
        if(k == 8){
            if(n == 1) return "8";
            if(n == 2) return "88";
            if(n == 3) return "888";
            if(n == 4) return "8888";
            if(n == 5) return "88888";
            if(n == 6) return "888888";
            for(int i = 0; i < n; i++) ans += '8';
            if(n >= 7){
                for(int i = 3; i < n-3; i++){
                    ans[i] = '9';
                }
                return ans;
            }
        }
        if(k == 9){
            for(int i = 0; i < n; i++) ans += '9';
            return ans;
        }
        return "";
    }
};
