class Solution {
public:
    // 6 7 8
    bool is7(const string& num) {
        int rem = 0;
        for (char c : num) {
            rem =(rem * 10 + (c - '0')) % 7;
        }
        return rem==0;
    }

    string dec(string s) {
        int i = s.length() - 1;
        while (i>= 0 && s[i]=='0') {
            s[i]='9';
            i--;
        }
        if (i>=0) s[i]--;
        return s;
    }
    string lar(int n){
        if (n == 1) return "7";
        if (n == 2) return "77";

        string lt(n/2, '9');
        int mid = (n % 2 == 1) ? 9 : -1;

        while (true) {
            string num = lt;
            if (mid != -1) num += to_string(mid);
            num += string(lt.rbegin(), lt.rend());

            if (is7(num)) return num;
            if (mid != -1) {
                mid--;
                if (mid < 0) {
                    mid = 9;
                    lt = dec(lt);
                }
            } else {
                lt = dec(lt);
            }
        }
    }
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(k==1 || k==3 || k==9){
            for(int i=0; i<n; i++) ans += '9';
            return ans;
        }
        else if(k==4){
            if(n==1) return "8";
            if(n==2) return "88";
            if(n==3) return "888";
            if(n==4) return "8888";
            ans += "88";
            for(int i=0; i<n-4; i++) ans += '9';
            ans += "88";

        }
        else if(k==2 || k==4){
            ans += '8';
            for(int i=0; i<n-2; i++) ans += '9';
            if(n!=1) ans += '8';
        }
        else if(k==5){
            ans += '5';
            for(int i=0; i<n-2; i++) ans += '9';
            if(n!=1) ans += '5';
        }
        else if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            if(n==3) return "888";
            if(n==4) return "8778";

            int sum = 16;
            string tmp = "8";
            ans += "8";

            bool odd = false;
            if(n%2!=0){
                n--;
                odd = true;
            } else{
                n -= 2;
            }
            for(int i=0; i<n-2; i=i+2){
                ans += '9';
                tmp = '9' + tmp;
            }

            if(odd){
                ans = ans + "8" + tmp;
            }
            else{
                ans = ans + "77" + tmp;
            }
        }
        else if(k==8){
            if(n<7) {
                for(int i=0; i<n; i++){
                    ans += '8';
                }
                return ans;
            }
            else{
                ans += "888";
                for(int i=0; i<n-6; i++) ans += '9';
                ans += "888";
                return ans;
            }
        }
        else{
            ans = lar(n);
        }

        return ans;
    }
};
