class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ret(n, '9');
         int val = 0;
         vector<int> factor {1,3,2,-1,-3,-2};
        switch (k) {
            case 2:
                ret[0] = '8';
                ret.back() = '8';
                break;
            case 4:
                ret[0] = '8';
                ret.back() = '8';
                if (n > 2) {
                    ret[1] = '8';
                    ret[n-2] = '8';
                }
                break;
            case 5:
                ret[0] = '5';
                ret.back() = '5';
                break;
            case 6:
                if (n==1)
                    ret = "6";
                else if (n==2)
                    ret = "66";
                else if (n > 2) {
                    ret[0] = '8';
                    ret.back() = '8';
                    if (n%2) ret[n/2] = '8';
                    else {
                        ret[n/2] = '7';
                        ret[n/2-1] = '7';
                    }
                }
                break;
            case 7:
                if (n%2) {
                    for (int i = 0; i < n; ++i) {
                        if (i == n/2)
                            continue;
                        val += factor[i%6] * 9;
                    }
                    for (int t = 9; t >= 0; --t)
                        if ((factor[(n/2)%6] * t + val)%7==0) {
                            ret[n/2] = t + '0';
                            break;
                        }
                }
                else {
                    for (int i = 0; i < n; ++i) {
                        if (i == n/2-1 || i == n/2)
                            continue;
                        val += factor[i%6] * 9;
                    }
                    for (int t = 9; t >= 0; --t)
                        if ((val + (factor[(n/2-1)%6] + factor[(n/2)%6]) * t)%7 == 0) {
                            ret[n/2-1] = t+'0';
                            ret[n/2] = t+'0';
                            break;
                        }
                }
                break;
            case 8:
                ret[0] = '8';
                ret.back() = '8';
                if (n > 2) {
                    ret[1] = '8';
                    ret[n-2] = '8';
                    if (n > 3) {
                        ret[2] = '8';
                        ret[n-3] = '8';
                    }
                }
                break;
        }
        return ret;
    }
};
