#define ll long long
class Solution {
public:
    bool isPalindrome(int num)
    {
        int original = num, reversed = 0;
        while (num > 0)
        {
            int digit = num % 10;
            reversed = reversed * 10 + digit;
            num /= 10;
        }
        return original == reversed;
    }
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9) {
            string s;
            for(int i = 0 ; i < n ; i++) {
                s += '9';
            }
            return s;
        }
        if(k == 2) {
            string s;
            for(int i = 0 ; i < n ; i++) {
                s += '9';
            }
            s[0] = '8', s[n-1] = '8';
            return s;
        }
        if(k == 4) {
            string s;
            for(int i = 0 ; i < n ; i++) {
                s += '8';
            }
            for(int i = 2 ; i < n -2 ; i++) {
                s[i] = '9';
            }
            return s;
        }
        if(k == 5) {
            string s;
            for(int i = 0 ; i < n ; i++) {
                s += '9';
            }
            s[0] = '5', s[(int)s.size()-1] = '5';
            return s;
        }
        if(k == 6) {
            string s;
            if(n <= 2) {
                for(int i = 0 ; i < n ; i++) {
                    s += '6';
                }
                return s;
            } else {
                for(int i = 0 ; i < n ; i++) {
                    s += '9';
                }  
                if(n%2) { 
                    s[n-1] = '8', s[0] = '8', s[n/2] = '8';
                    return s;
                } else {
                    s[n-1] = '8', s[0] = '8', s[n/2] = '7', s[(n/2)-1] = '7';
                }
                return s;
            }
        }
        if(k == 7) {
            string s;
            for(int i = 0 ; i < n ; i++) {
                s += '9';
            }
            if(n%2) {
                for(char i = '9' ; i >= '0' ; i--) {
                    s[n/2] = i;
                    if (divisibilityArray(s, 7)) {
                        return s;
                    }
                }
            } else {
                for(char i = '9' ; i >= '0' ; i--) {
                    s[n/2] = i, s[(n/2)-1] = i;
                    if (divisibilityArray(s, 7)) {
                        return s;
                    }
                }
            }
            // for(int i = 0 ; i < 1e5 ; i++) {
            //     if(isPalindrome(i) && i%7 == 0)
            //     cout << i << " ";
            // }
        }
        if(k == 8) {
            string s;
            for(int i = 0 ; i < n ; i++) {
                s += '8';
            }       
            if(n<=6) {
                return s;
            } else{
                for(int i = 3 ; i < n- 3 ; i++) {
                    s[i] = '9';
                }
                return s;
            }
        }
        return "1";
    }
    bool divisibilityArray(string word, int m) {
        int oaidpe = 0;
        ll kcjvd = 0;
        int ladk = word.size();
        for (int i = 0; i < ladk; i++){
            kcjvd = (kcjvd * 10 + (int)word[i] - '0') % m;
            oaidpe = (kcjvd == 0 ? 1 : 0);
        }
        return oaidpe == 1;
    }
};
