class Solution {
    public String largestPalindromeDivisibleBy7(int n, String ans) {
        if (n == 1) return "7";
        else if (n == 2) return "77";
        else if (n == 3) return "959";
        else if (n == 4) return "9779";
        else if (n == 5) return "99799";
        else if (n == 6) return "999999";
        else {
            if (n % 6 == 0) return ans;
            else {
                if ((n / 6) % 2 == 0) {
                    if (n % 6 == 1 || n % 6 == 5) {
                        ans = ans.substring(0, n / 2) + '7' + ans.substring(n / 2 + 1);
                        return ans;
                    } else if (n % 6 == 2 || n % 6 == 4) {
                        ans = ans.substring(0, n / 2 - 1) + "77" + ans.substring(n / 2 + 1);
                        return ans;
                    } else {
                        ans = ans.substring(0, n / 2) + '5' + ans.substring(n / 2 + 1);
                        return ans;
                    }
                } else {
                    if (n % 6 == 1 || n % 6 == 5) {
                        ans = ans.substring(0, n / 2) + '4' + ans.substring(n / 2 + 1);
                        return ans;
                    } else if (n % 6 == 2 || n % 6 == 4) {
                        ans = ans.substring(0, n / 2 - 1) + "44" + ans.substring(n / 2 + 1);
                        return ans;
                    } else {
                        ans = ans.substring(0, n / 2) + '6' + ans.substring(n / 2 + 1);
                        return ans;
                    }
                }
            }
        }
    }

    public String largestPalindrome(int n, int k) {
        StringBuilder ans = new StringBuilder();
        for (int i = 0; i < n; i++) ans.append("9");

        if (k == 1) {
            return ans.toString();
        } else if (k == 2) {
            ans.setCharAt(0, '8');
            ans.setCharAt(n - 1, '8');
            return ans.toString();
        } else if (k == 3) {
            return ans.toString();
        } else if (k == 4) {
            if (n == 1) return "8";
            ans.setCharAt(0, '8');
            ans.setCharAt(1, '8');
            ans.setCharAt(n - 1, '8');
            ans.setCharAt(n - 2, '8');
            return ans.toString();
        } else if (k == 5) {
            ans.setCharAt(0, '5');
            ans.setCharAt(n - 1, '5');
            return ans.toString();
        } else if (k == 6) {
            if (n == 1) return "6";
            else if (n == 2) return "66";
            else if (n == 3) return "888";
            else {
                if (n % 2 == 1) {
                    ans.setCharAt(0, '8');
                    ans.setCharAt(n - 1, '8');
                    ans.setCharAt(n / 2, '8');
                    return ans.toString();
                } else {
                    ans.setCharAt(0, '8');
                    ans.setCharAt(n - 1, '8');
                    ans.setCharAt(n / 2, '7');
                    ans.setCharAt(n / 2 - 1, '7');
                    return ans.toString();
                }
            }
        } else if (k == 7) {
            return largestPalindromeDivisibleBy7(n, ans.toString());
        } else if (k == 8) {
            if (n == 3) return "888";
            else if (n == 2) return "88";
            else if (n == 1) return "8";
            else {
                ans.setCharAt(0, '8');
                ans.setCharAt(1, '8');
                ans.setCharAt(2, '8');
                ans.setCharAt(n - 1, '8');
                ans.setCharAt(n - 2, '8');
                ans.setCharAt(n - 3, '8');
                return ans.toString();
            }
        } else if (k == 9) {
            return ans.toString();
        }

        return "";
    }
}
