class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # if k==1 or 3 or 9. return [9]*n
        # if k==2 return 8,9*(n-2),8
        # if k==4 return 8,8,9s,8,8
        # if k==5 return 5,9s,5
        # if k==6 return 8,,8

        if k==1 or k==3 or k==9:
            return "9"*n
        if k==2:
            if n==1:
                return "8"
            else:
                return "8"+"9"*(n-2)+"8"
        if k==5:
            if n==1:
                return "5"
            else:
                return "5"+"9"*(n-2)+"5"
        if k==4:
            if n<=4:
                return "8"*n
            else:
                return "8"*2+"9"*(n-4)+"8"*2
        if k==6:
            if n<=2:
                return "6"*n
            if n%2==1:
                return "8"+"9"*((n-3)//2)+"8"+"9"*((n-3)//2)+"8"
            return "8"+"9"*((n-4)//2)+"77"+"9"*((n-4)//2)+"8"
        if k==7:
            if n<=2:
                return "7"*n
            num = "9"*n
            numrem = 0
            for i in range(n):
                numrem*=10
                numrem+=9
                numrem%=7
            curr = 1
            for i in range((n-1)//2):
                curr*=10
                curr%=7
            if n%2==1:
                for j in range(9):
                    if (numrem-curr*j)%7==0:
                        break
                return "9"*((n-1)//2)+str(9-j)+"9"*((n-1)//2)
            else:
                curr = (curr*11)%7
                for j in range(9):
                    if (numrem-curr*j)%7==0:
                        break
                return "9"*((n-2)//2)+str(9-j)*2+"9"*((n-2)//2)

        if k==8:
            if n<=6:
                return "8"*n
            else:
                return "8"*3+"9"*(n-6)+"8"*3
