class Solution {
public:
    bool f(const std::string& num) {
    int remainder = 0;
    for (char digit : num) {
        remainder = (remainder * 10 + (digit - '0')) % 7;
    }
    return remainder == 0;
    }
    string largestPalindrome(int n, int k) {
        string s;
        for(int i=0;i<n;i++){
            s+='#';
        }
        if(k==1){
            for(int i=0;i<n;i++){
                s[i]='9';
            }
        }else if(k==2){
           int i=0;
           int j=n-1;
           s[i]='8';
           s[j]='8';
           i++;
           j--;
           while(i<=j){
            s[i]='9';
            s[j]='9';
            i++;
            j--;
           }
        }else if(k==3){
            for(int i=0;i<n;i++){
                s[i]='9';
            }
        }else if(k==5){
             int i=0;
           int j=n-1;
           s[i]='5';
           s[j]='5';
           i++;
           j--;
           while(i<=j){
            s[i]='9';
            s[j]='9';
            i++;
            j--;
           }
        }else if(k==9){
            for(int i=0;i<n;i++){
                s[i]='9';
            }
        }else if(k==4){
            if(n==1){
                s="8";
            }else if(n==2){
                s="88";
            }else if(n==3){
                s="888";
            }else{
                s[0]='8';
                s[1]='8';
                s[n-1]='8';
                s[n-2]='8';
                int i=2;
                int j=n-3;
                while(i<=j){
                    s[i]='9';
                    s[j]='9';
                    i++;
                    j--;
                }
            }
        }else if(k==8){
            if(n<=6){
                for(int i=0;i<n;i++){
                    s[i]='8';
                }
            }else{
                s[0]='8';
                s[1]='8';
                s[2]='8';
                s[n-1]='8';
                s[n-2]='8';
                s[n-3]='8';
                int i=3;
                int j=n-4;
                while(i<=j){
                    s[i]='9';
                    s[j]='9';
                    i++;
                    j--;
                }
            }
        }else if(k==6){
            if(n==1){
               s[0]='6';
            }else if(n==2){
                s[0]='6';
                s[1]='6';
            }else{
                if(n%2){
                    s[0]='8';
                    s[n-1]='8';
                    int i=1;
                    int j=n-2;
                    while(i<j){
                        s[i]='9';
                        s[j]='9';
                        i++;
                        j--;
                    }
                    s[i]='8';
                }else{
                    s[0]='8';
                    s[n-1]='8';
                    int i=1;
                    int j=n-2;
                    while(j-i>1){
                        s[i]='9';
                        s[j]='9';
                        i++;
                        j--;
                    }
                    s[i]='7';
                    s[j]='7';
                }
            }
        }else{
            if(n==1){
                s="7";
            }else if(n==2){
                s="77";
            }else{
                if(n%2){
                    int i=0;
                    int j=n-1;
                    while(i<j){
                        s[i]='9';
                        s[j]='9';
                        i++;
                        j--;
                    }
                    for(char x='9';x>='0';x--){
                        s[i]=x;
                        if(f(s)){
                            break;
                        }
                    }

                }else{
                    int i=0;
                    int j=n-1;
                    while(j-i>1){
                        s[i]='9';
                        s[j]='9';
                        i++;
                        j--;
                    }
                    for(char x='9';x>='0';x--){
                        s[i]=x;
                        s[j]=x;
                        if(f(s)){
                            break;
                        }
                    }
                }
            }
        }
        return s;
    }
};
